//--------------For Pre-Calculate & Light----------------
float RadicalInverse(uint bits) {
    bits = (bits << 16u) | (bits >> 16u);
    bits = ((bits & 0x55555555u) << 1u) | ((bits & 0xAAAAAAAAu) >> 1u);
    bits = ((bits & 0x33333333u) << 2u) | ((bits & 0xCCCCCCCCu) >> 2u);
    bits = ((bits & 0x0F0F0F0Fu) << 4u) | ((bits & 0xF0F0F0F0u) >> 4u);
    bits = ((bits & 0x00FF00FFu) << 8u) | ((bits & 0xFF00FF00u) >> 8u);
    return float(bits) * 2.3283064365386963e-10f;
}
vec2 Hammersley(uint i, uint N) {
    return vec2(float(i) / float(N), RadicalInverse(i));
}
vec3 ImportanceSampleGGX(vec2 Xi, vec3 N, float roughness){
    float a = roughness*roughness;

    float phi = 2.0 * PI * Xi.x;
    float cosTheta = sqrt((1.0 - Xi.y) / (1.0 + (a*a - 1.0) * Xi.y));
    float sinTheta = sqrt(1.0 - cosTheta*cosTheta);

    // from spherical coordinates to cartesian coordinates
    vec3 H;
    H.x = cos(phi) * sinTheta;
    H.y = sin(phi) * sinTheta;
    H.z = cosTheta;

    // from tangent-space vector to world-space sample vector
    vec3 up        = abs(N.z) < 0.999 ? vec3(0.0, 0.0, 1.0) : vec3(1.0, 0.0, 0.0);
    vec3 tangent   = normalize(cross(up, N));
    vec3 bitangent = cross(N, tangent);

    vec3 sampleVec = tangent * H.x + bitangent * H.y + N * H.z;
    return normalize(sampleVec);
} 
//the Schlick model
float GeometrySchlickGGX_IBL(float NdotV, float roughness){
    float a = roughness;
    float k = (a * a) / 2.0;

    float nom   = NdotV;
    float denom = NdotV * (1.0 - k) + k;
    return nom / denom;
}
float GeometrySchlickGGX_LIGHT(float NdotV, float roughness){
    float r = (roughness + 1.0);
    float k = (r*r) / 8.0;

    float nom   = NdotV;
    float denom = NdotV * (1.0 - k) + k;
    return nom / denom;
}
float GeometrySmith(vec3 N, vec3 V, vec3 L, float roughness,bool PRE_CALCULATE_IBL){
    float NdotV = max(dot(N, V), 0.0);
    float NdotL = max(dot(N, L), 0.0);
    float ggx1,ggx2;
    if(PRE_CALCULATE_IBL){
    	ggx2 = GeometrySchlickGGX_IBL(NdotV, roughness);
    	ggx1 = GeometrySchlickGGX_IBL(NdotL, roughness);
    }
    else{
		ggx2 = GeometrySchlickGGX_LIGHT(NdotV, roughness);
    	ggx1 = GeometrySchlickGGX_LIGHT(NdotL, roughness);
    }
    return ggx1 * ggx2;
}
float DistributionGGX(vec3 N, vec3 H, float roughness){
    float a = roughness*roughness;
    float a2 = a*a;
    float NdotH = max(dot(N, H), 0.0);
    float NdotH2 = NdotH*NdotH;

    float nom   = a2;
    float denom = (NdotH2 * (a2 - 1.0) + 1.0);
    denom = PI * denom * denom;

    return nom / denom;
}

//Schlick Fresnel
vec3 fresnelSchlick(float cosTheta, vec3 F0){
    return F0 + (1.0 - F0) * POW5(1.0 - cosTheta);
}
//Schlickâ€™s Fresnel Approximation by Spherical Gaussian approximation
vec3 fresnelSchlickApproximation(float costheta, vec3 F0){
	return F0 + (1.-F0)*exp2((-5.55473*costheta-6.98316)*costheta);
}
vec3 fresnelSchlickRoughness(float cosTheta, vec3 F0, float roughness){
    return F0 + (max(vec3(1.0 - roughness), F0) - F0) * pow(1.0 - cosTheta, 5.0);
}

//------------------Post Process---------------
vec3 SphereTexture(sampler2D tex,vec3 normal) {
     float u = atan(normal.z, normal.x) / PI * 2.0;
     float v = asin(normal.y) / PI * 2.0;
     return texture(tex,vec2(u,v)).rgb;
}
//from Blender Eevee math function
vec3 Tonemap(vec3 color,float gamma,float luma){
    vec3 c = exp(-1.0 / (2.72 * color + 0.15));
    c = pow(c, vec3(1.0 / (gamma * luma)));
    return c;
}
vec3 HDR(vec3 color,float gamma) {
    float luma = dot(color, vec3(0.2126, 0.7152, 0.0722));
    return mix(color, Tonemap(color,gamma, luma), 1.0 - luma);
}

//-----------------------Sampler----------------------
ivec2 iU_Mirror(sampler2D _tex,ivec2 iU,int lod){
	ivec2 size = textureSize(_tex,lod);
    return abs(((iU/size)&1)*(size-1)-(iU%size));
}
vec3 TexelFetch_Mirror(sampler2D _tex,ivec2 iU,int lod){
    return texelFetch(_tex,iU_Mirror(_tex,abs(iU),0),lod).rgb;
}
//------------------------PBR Texture Filters--------------------------
//from my "StructBuffer" https://www.shadertoy.com/view/Mtyyzd
float GetLuminance(vec3 col){
	return dot(col,vec3(0.2126729,0.7151522,0.0721750));
}
/*
	Reference:
	https://web.eecs.umich.edu/~sugih/courses/eecs487/lectures/26-BumpMap+ProcTex.pdf
	https://squircleart.github.io/shading/normal-map-generation.html
	Tangent Space Normal Maps
	Note: I just use forward finite difference enough for fast
*/
#define GetTextureLuminance(_Tex,uv) GetLuminance(texture(_Tex,uv).rgb)  
vec3 FastNormalFilter(sampler2D _tex,vec2 uv,float strength,vec2 offset){
	vec3 e = vec3(offset,0.);
    float p00 = GetTextureLuminance(_tex,uv);
    float p10 = GetTextureLuminance(_tex,uv + e.xz);
    float p01 = GetTextureLuminance(_tex,uv + e.zy);
    /* Orgin calculate 
    vec3 ab = vec3(1.,0.,p10-p00);
    vec3 ac = vec3(0.,1.,p01-p00);
    vec3 n = cross(ab,ac);
    n.z *= (1.-strength);
    return normalize(n);
	*/
	vec2 dir = p00-vec2(p10,p01);
    return normalize(vec3(dir,1.-strength));
}
vec3 SuperFastNormalFilter(sampler2D _tex,vec2 uv,float strength){
    float p00 = GetTextureLuminance(_tex,uv);
    return normalize(vec3(-dFdx(p00),-dFdy(p00),1.-strength));
}

//----------------------Pre-Calculate Specular IBL---------------------
#define PRE_CALCULATE_IBL
#define Cubemap_Texture iChannel0

float ID = 0.;
vec3 getAxis(vec3 _dir){
    vec3 dir = abs(_dir);
	//float min_a = min(dir.x,min(dir.y,dir.z));
    vec3 vec_xyz = dir - min(dir.x,min(dir.y,dir.z)); 
    vec3 vec_T_xyz = vec_xyz-(vec_xyz.x+vec_xyz.y+vec_xyz.z)*0.5;
    return sign(clamp(vec_T_xyz,0.,1.))*sign(_dir);
}
vec3 GetUVW(vec3 axis,vec3 dir){
    return 1./dot(axis,dir)*dir-axis;
}
vec3 GetMipMapUVW_Dir(vec3 _uvw,vec3 _axis){
    _uvw = floor((_uvw+1.)*512.)+0.5;
    vec3 a = exp2(floor(log2(_uvw)));
    return normalize((_uvw*2./a - 3.)*(1.-abs(_axis))+_axis);
}
vec3 PrefilterEnvMap( float Roughness,vec3 Dir ) { 
	vec3 N = Dir; 
	vec3 V = Dir;
	vec3 PrefilteredColor = vec3(0.);
    float TotalWeight = 0.;
	const uint NumSamples = 1024u; 
	for( uint i = 0u; i < NumSamples; i++ ) { 
		vec2 Xi = Hammersley( i, NumSamples ); 
		vec3 H = ImportanceSampleGGX( Xi, N, Roughness); 
		vec3 L = reflect(-V,H);//2. * dot( V, H ) * H - V;
		float NoL = dot( N, L ); 
		if( NoL > 0. ) { 
			PrefilteredColor += texture(Cubemap_Texture,L).rgb * NoL; 
			TotalWeight += NoL; 
		}
	}
	return PrefilteredColor / TotalWeight;
}
vec3 GetMipMapUVW_Dir2(vec3 _uvw,vec3 _axis){
    ivec3 xyz = ID_xyz[int(dot(abs(_axis.yz),vec2(1,2)))];
	vec3 uv = vec3(_uvw[xyz.x],_uvw[xyz.y],_uvw[xyz.z]);
    uv.xy = floor((uv.xy+1.)*512.);
    vec2 a = ceil(log2(uv.xy+1.));
	float max_a = max(a.x,a.y);
    float scale = exp2(max_a-1.);
    vec2 B = step((a.x+a.y)/2.,a);
    ID = max_a*3.-3. + B.y*2. + B.x;
    uv.xy = ((uv.xy+0.5)/scale - B - 0.5)*2.;
    uv = vec3(uv[xyz.x],uv[xyz.y],uv[xyz.z]);
    return normalize(uv+_axis);
}

