//--------------Store Struct Buffer-------------------
struct StructBuffer{
    vec3 imgA;
    vec3 imgB;
    vec3 imgC;
    vec3 imgD;
    mediump vec2 other;
};
vec4 StoreStructBuffer(StructBuffer buffer){
    mat3x4 stream = mat3x4(buffer.imgA,buffer.imgB,buffer.imgC,buffer.imgD);
    vec4 store;
    /*------store.xyz all RGB 8bits images--------*/
    for(int i=0;i<3;i++){
    	uvec4 data = (uvec4(stream[i]*255.+.5)&0xFFu)<<ivec4(24,16,8,0);
        store[i] = uintBitsToFloat(data.x|data.y|data.z|data.w);
    }
    /*-----store.w other 2x16bits float values------*/ 
    store.w = uintBitsToFloat(packHalf2x16(buffer.other));
    return store;
}
