void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	fragColor = vec4(vec3(1.0-texture(iChannel0, uv).a), 1.0);
}

#define BRUSH_SIZE 20.0
#define STROKE_LENGTH 800.0

#define dt 0.20

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}
float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 	vec2 uv = fragCoord/iResolution.xy;   

    float brushStrength = texture(iChannel0, vec2(0.5, 0.0)).a;

    vec4 col = texture(iChannel0, uv); 

    vec2 w = 1.0/iResolution.xy;

    vec4 tr = texture(iChannel0, uv + vec2(w.x , 0), 0.0);
    vec4 tl = texture(iChannel0, uv - vec2(w.x , 0), 0.0);
    vec4 tu = texture(iChannel0, uv + vec2(0 , w.y), 0.0);
    vec4 td = texture(iChannel0, uv - vec2(0 , w.y), 0.0);

	vec3 dx = (tr.xyz - tl.xyz)*0.5;
	vec3 dy = (tu.xyz - td.xyz)*0.5;
	vec2 densDif = vec2(dx.z ,dy.z);

    const float K = 0.2;
	const float v = 0.55;

    col.z -= dt*dot(vec3(densDif, dx.x + dy.y) ,col.xyz);
    vec2 laplacian = tu.xy + td.xy + tr.xy + tl.xy - 4.0*col.xy;
    vec2 viscForce = vec2(v)*laplacian;
    col.xyw = texture(iChannel0, uv - dt*col.xy*w, 0.).xyw;

	vec2 oldBrushPos = vec2(texture(iChannel0, vec2(0.0, 0.5)).a,
                                texture(iChannel0, vec2(0.9999, 0.5)).a);

	vec2 dirVec = iMouse.xy -  oldBrushPos;    
    vec4 brushColor = vec4(dirVec.x, dirVec.y, 0.0, 1.0);
	float brushStregthRamp = clamp(sin(brushStrength*3.1415926), 0.0, 1.0);

    for(int i=0; i < 20; i++) {
        vec2 mPos = mix(iMouse.xy, oldBrushPos, float(i)/20.0);
    	float d = 1.0 - smoothstep(0.0, 1.0, distance(fragCoord, mPos) / BRUSH_SIZE / brushStregthRamp);
        d *= smoothstep(0.2, 1.0, (noise(fragCoord-mPos)+0.75)/5.0) * 10.0;               
        float bd = 1.0 - smoothstep(0.0, 1.0, distance(fragCoord, mPos) / BRUSH_SIZE / brushStregthRamp / 1.5);
        bd *= smoothstep(0.1, 1.0, (noise(vec2(brushStrength * 20.0, 0.0) + (fragCoord-mPos)/2.0)+0.0)/5.0) * 10.0;               
        col = mix(col, brushColor, d + bd);
    }

    col.xyz *= 0.95;

    brushStrength -= distance(iMouse.xy, oldBrushPos) / STROKE_LENGTH;
    float oldMouseClick = texture(iChannel0, vec2(0.5, 0.9999)).a;
    if(iMouse.z != oldMouseClick) {
        if(oldMouseClick > 0.0) {
    		brushStrength = 1.0;
        } else {
            brushStrength = 0.0;
        }
    }
    brushStrength = clamp(brushStrength, 0.0, 1.0);

   vec4 oldMouseXCol = vec4(col.xyz, iMouse.x);
    vec4 oldMouseYCol = vec4(col.xyz, iMouse.y);
    vec4 brushStrengthCol = vec4(col.xyz, brushStrength);
    vec4 mouseClickCol = vec4(col.xyz, iMouse.z);

    col = mix(oldMouseXCol, col, smoothstep(1.0, 2.0, fragCoord.x));
    col = mix(col, oldMouseYCol, smoothstep(iResolution.x-2.0, iResolution.x, fragCoord.x));
	col = mix(brushStrengthCol, col, smoothstep(1.0, 2.0, fragCoord.y));
    col = mix(col, mouseClickCol, smoothstep(iResolution.y-2.0, iResolution.y, fragCoord.y));

    fragColor = col;
}
