float noise(in vec2 p) {
    return fract(sin(p.x * 17.6452 + p.y * 565.337) * 5425.235);
}

float SmoothNoise(in vec2 p) {
    vec2 luv = smoothstep(0.0, 1.0, fract(p)); // :P
    vec2 cid = floor(p);

    float bl = noise(cid);
    float br = noise(cid + vec2(1.0, 0.0));
    float tl = noise(cid + vec2(0.0, 1.0));
    float tr = noise(cid + vec2(1.0, 1.0));

    return mix(mix(bl, br, luv.x), mix(tl, tr, luv.x), luv.y);
}

float CoherentNoise(in vec2 p, in float scale, in float octaves) {
    float value = 0.0;
    float nscale = 1.0;
    float tscale = 0.0;

    for (float octave=0.0; octave < octaves; octave++) {
        value += SmoothNoise(p * pow(2.0, octave) * scale) * nscale;
        tscale += nscale;
        nscale *= 0.5;
    }

    return value / tscale;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 ro = vec3(0.0, 0.0, 5.0);
    vec3 rd = normalize(vec3(uv, -1.0));
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    float dt = 0.0;
    for (int iter=0; iter < 50; iter++) {
        vec3 p = ro + rd * dt;

        float c = cos(iTime), s = sin(iTime);
        p.xz *= mat2(c, -s, s, c);

        float d = length(p) - 2.0 + 0.15 * length(vec3(CoherentNoise(p.xy, 10.0, 4.0), CoherentNoise(p.xz, 10.0, 4.0), CoherentNoise(p.yz, 10.0, 4.0)));
        if (d < 0.001) {
            p *= 0.25;
            vec3 n = normalize(p);
            vec3 l = normalize(vec3(-1.0, 1.0, 1.0));
            l.xz *= mat2(c, -s, s, c);
            fragColor.rgb += max(0.0, dot(n, l) * 0.75 * length(vec3(CoherentNoise(p.xy, 10.0, 4.0), CoherentNoise(p.xz, 10.0, 4.0), CoherentNoise(p.yz, 10.0, 4.0))));
            break;
        }

        if (dt > 10.0) {
            break;
        }

        dt += d;
    }
}
