mat2 r2(float a) {
	float s = sin(a), c = cos(a);
	return mat2(c,-s,s,c);
}

vec4 r4(float n) {
	#define R4P 1.1673039782614187
    return fract(.5+vec4(1./R4P,1./R4P/R4P,1./R4P/R4P/R4P,1./R4P/R4P/R4P/R4P)*n);
}
vec4 hash(vec4 a) {
    a = mod(abs(a),8273.97234);
    #define R4S(sw) floor(fract(.352347+dot(a,vec4(.001,.1,10.,100.).sw*2.23454))*20000.)
    return r4(R4S(xyzw)+R4S(yzwx)+R4S(wxyz)+R4S(zwxy));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
#define time iTime
    vec2 uv = 200.+(fragCoord*2.-iResolution.xy)*(5.+time*.4)/iResolution.y;

	vec4 rh = hash(floor(uv.x)*vec4(.01111,.1111,1.1111,11.11111));
	vec2 p = uv.xy+vec2(0,(.7+rh.x)*time),
		fp = floor(p),
		rp = (p-fp)*2.-1.;
	vec4 bh = hash(fp.xyyx*.1111);
	if (bh.y > .75) rp.x = abs(rp.x);
	else if (bh.y > .5) rp.y = abs(rp.y);
	float d = 1e8;
	bh.w = 1.+bh.w*6.;
	for (float p = 0.; p < bh.w; p++) {
		vec4 h = hash(bh+fp.xyyx-p*vec4(.01,.1,1.,10.));
		d = min(d,length(max(abs((rp-(h.xy-.5))*r2(h.w*6.28))-vec2(.06,.2+pow(h.z,2.)*.6),0.)));
	}
	d = max(0.,1.-max(0.,d*iResolution.y/20.));
	fragColor = mix(vec4((vec3(.3,1.3,.6)+(rh.yzw*2.-1.)*.5)*d,1),texelFetch(iChannel0,ivec2(fragCoord),0),.95);
}
