float obj_infiniteBuildings(in vec3 p)
{
	vec3 c = vec3(12,10,20); // how close cubes are to each other
	vec3 f = vec3(8,10,7);

	vec3 q = p;
	//repetition in x and z direction
	q.x = mod(p.x,c.x)-0.5*c.x;
	q.x = mod(q.x,f.x)-0.5*f.x;

	q.z = mod(p.z,c.z)-0.5*c.z;
	q.z = mod(q.z,f.z)-0.5*f.z;

	//building height
	vec3 b1 = vec3(1.0, 3.0, 1.0);
    vec3 b2 = vec3(2.0, 3.0, 2.0);

	float s1= sdBox(q-vec3(0.0,5.0,0.0),b1);	
    float s2= sdBox(q,b2);	
    return unionSDF(s1, s2);
