vec4 color (vec2 U, float q) {

vec4 c = C(U), a = A(c.zw), cc = B(c.zw);
    for (int x = -1; x<=1;x++)
        for (int y = -1; y<=1;y++){
        	vec4 ccc = A(c.zw+O*vec2(x,y));
            if (length(a.xy-A(ccc.xy).xy)<length(a.xy-A(cc.xy).xy))
                cc=ccc;
        }
    vec4  b = A(cc.xy),
         aa = (c.zwzw), bb = (cc.xyxy);
    vec2 r = a.xy-b.xy, p = aa.xy-bb.xy;
    float an = angle(r,p);

   	vec2 u = U-a.xy;
    vec4 t = B(c.zw+u);
    t = A(t.xy);
    float s = sg(U,a.xy,t.xy);
    vec4 Q = D(aa.xy);
    Q.x = floor(Q.w/1e4)/1e2;
    Q.y = floor(mod(Q.w,1e4)/1e2)/1e2;
    Q.z = floor(mod(Q.w,1e2))/1e2;
    if (aa.x<0.5*R.x) Q = Q.xzyw;
    u *= mat2(cos(an),sin(-an),sin(an),cos(an));
    u = 2.*abs(u);
    Q *= vec4(1)*smoothstep((1.+q)*O,(1.-q)*O,max(u.x,u.y));
	return Q;
}

Main {
    vec4 c1 = color(U,0.1), c2 = color(U+3.,.4), c3 = vec4(U/R*2.-3.,0,1);
    for (int i = 0; i < 8; i++) {
    	c3.yz *= mat2(cos(.2*iTime),sin(-.2*iTime),sin(.2*iTime),cos(.2*iTime));
        c3 = abs(c3/dot(c3.xyz,c3.xyz)-.2)-.2;
        c3.zx *= mat2(cos(.9),sin(-.9),sin(.9),cos(.9));
    	c3=4.*sin(c3);
    }
    c3.xyz *= mat3(.6,.7,.7,.3,.6,.1,-.4,.9,.1);
    Q = 0.15+0.85*c1+max(.1*c3-10.*length(c1),0.);
    Q -= .1*length(c2)*max(1.-10.*c1,0.);

}

vec2 force (vec2 U, vec4 a, vec2 bb) {
    vec4 b = A(bb);
    if (b.x<5.&&b.y<5.||bb.x<5.||bb.y<5.) return vec2(0);

    vec2 r = b.xy-a.xy, v = b.zw-a.zw;
    vec4 da = D(U), db = D(bb);
	vec2 q = da.xy-db.xy;
    float l = length(r), j = length(q);
    if (l < 1e-9) return vec2(0);
    vec2 f = vec2(0);
    if (l!=0.&&j!=0.){
    if (sign(U.x-0.5*R.x)==sign(bb.x-0.5*R.x)&&(l-j)<O*3.)
            f = r/l*(l-j)*exp(-0.01*j);
    else f = -30.*smoothstep(1.5*O,0.5*O,l)*r/l;
    }
    return f;
}
Main {
    vec2 u = shape(U);
    Q = A(u);
    if ((iFrame>100&&Q.x<1.&&Q.y<1.)||u.x+O>=R.x||u.y+O>=R.y||u.x<O||u.y<O||length(U-u)>1.) {Q=-1e8*R.xyxy;return;}

    vec2 f = vec2(0);
    for (int x=-3;x<3; x++) {
        for (int y=-3;y<3; y++) {
            vec4 b = B(u+vec2(x,y));
            f += (
                force(U,Q,b.xy)+
                force(U,Q,b.zw));
        }
	}
    for (int i=-10;i<=10;i++) {
    	f+=force(U,Q,U+3.*O*vec2(i,0));
    	f+=force(U,Q,U+3.*O*vec2(0,i));
    }
    f /= 100.;
    Q.zw += f;
    Q.xy += f+Q.zw*sqrt(7./(1.+dot(Q.zw,Q.zw)));
    if (length(Q.zw)>3.)Q.zw = 3.*normalize(Q.zw);
    if (iMouse.z>0.) Q.zw += 100.*(Q.xy-iMouse.xy)/dot(Q.xy-iMouse.xy,Q.xy-iMouse.xy);

    	if (Q.x<5.) {Q.x=5.;Q.z = +abs(Q.z);}
        if (R.x-Q.x<5.) {Q.x=R.x-5.;Q.z =-abs(Q.z);}
        if (Q.y<5.) {Q.y=5.;Q.z=0.;Q.w = +abs(Q.w);}
        if (R.y-Q.y<5.) {Q.y=R.y-5.;Q.w =-abs(Q.w);}

    if (iFrame < 102) {
    	Q = vec4(D(U).xy,1.8,1.4);
    }

}

void X (inout vec4 Q, inout vec2 r, vec4 a, vec2 U, vec2 u, vec4 n) {
	float l = line(a.xy+U-u,a.xy,A(n.xy).xy),
          ll = line(a.xy+U-u,a.xy,A(n.zw).xy);
    if (l<r.x){
        r = vec2(l,r.x);
        Q=vec4(n.xy,Q.xy);
    } else if (l<r.y) {
    	r.y = l;
        Q.zw = n.xy;
    }
    if (ll<r.x){
    	r = vec2(ll,r.x);
        Q=vec4(n.zw,Q.xy);
    } else if (ll<r.y) {
    	r.y = ll;
        Q.zw = n.zw;
    }
}
Main {
    vec2 u = shape(U);
    vec4 a = A(u);
	Q = B(U);
    vec2 r = vec2(line(a.xy+U-u,a.xy,A(Q.xy).xy)
                 ,line(a.xy+U-u,a.xy,A(Q.zw).xy));


    X(Q,r,a,U,u,B(U+vec2(1,1)));
    X(Q,r,a,U,u,B(U+vec2(0,1)));
    X(Q,r,a,U,u,B(U+vec2(1,0)));
    X(Q,r,a,U,u,B(U-vec2(0,1)));
    X(Q,r,a,U,u,B(U-vec2(1,0)));
    X(Q,r,a,U,u,B(U+vec2(1,1)));
    X(Q,r,a,U,u,B(U+vec2(1,-1)));
    X(Q,r,a,U,u,B(U-vec2(1,1)));
    X(Q,r,a,U,u,B(U-vec2(1,-1)));

    X(Q,r,a,U,u,B(Q.xy-U+u));
    X(Q,r,a,U,u,B(Q.zw-U+u));
    X(Q,r,a,U,u,B(Q.xy-U+u+vec2(0,1)));
    X(Q,r,a,U,u,B(Q.xy-U+u+vec2(1,0)));
    X(Q,r,a,U,u,B(Q.xy-U+u-vec2(0,1)));
    X(Q,r,a,U,u,B(Q.xy-U+u-vec2(1,0)));
    X(Q,r,a,U,u,B(Q.xy-U+u+vec2(1,1)));
    X(Q,r,a,U,u,B(Q.xy-U+u+vec2(1,-1)));
    X(Q,r,a,U,u,B(Q.xy-U+u-vec2(1,1)));
    X(Q,r,a,U,u,B(Q.xy-U+u-vec2(1,-1)));

    X(Q,r,a,U,u,C(a.xy+2.*vec2(O,0)).zwxy);
    X(Q,r,a,U,u,C(a.xy+2.*vec2(0,O)).zwxy);
    X(Q,r,a,U,u,C(a.xy-2.*vec2(O,0)).zwxy);
    X(Q,r,a,U,u,C(a.xy-2.*vec2(0,O)).zwxy);
}

void Y (inout vec4 Q, vec2 U, vec4 n) {
	float l = length(U-A(n.xy).xy),//sg(U,A(n.xy).xy,A(n.zw).xy),
          ll = length(U-A(Q.xy).xy);//sg(U,A(Q.xy).xy,A(Q.zw).xy);
    if (l<ll) Q.xy = n.xy;
}
void X (inout vec4 Q, vec2 U, vec4 n) {
	float l = length(U-A(n.zw).xy),//sg(U,A(n.xy).xy,A(n.zw).xy),
          ll = length(U-A(Q.zw).xy);//sg(U,A(Q.xy).xy,A(Q.zw).xy);
    if (l<ll) Q.zw = n.zw;
}
Main {
	Q = C(U).xyxy;
    if (iFrame%N==0) Q.xy = U;
    else {
        float k = exp2(float(N-1-(iFrame%N)));
    	Y(Q,U,C(U+vec2(0,k)));
    	Y(Q,U,C(U+vec2(k,0)));
    	Y(Q,U,C(U-vec2(0,k)));
    	Y(Q,U,C(U-vec2(k,0)));
    }

	X(Q,U,C(U+vec2(0,1)));
    X(Q,U,C(U+vec2(1,0)));
    X(Q,U,C(U-vec2(0,1)));
    X(Q,U,C(U-vec2(1,0)));

    X(Q,U,C(U+vec2(3,3)));
    X(Q,U,C(U+vec2(3,-3)));
    X(Q,U,C(U-vec2(3,3)));
    X(Q,U,C(U-vec2(3,-3)));

    X(Q,U,C(U+vec2(0,4)));
    X(Q,U,C(U+vec2(4,0)));
    X(Q,U,C(U-vec2(0,4)));
    X(Q,U,C(U-vec2(4,0)));

    X(Q,U,C(U));
}

Main {
	Q.xy = shape(U);
    //Q.x += 0.5*O*mod(Q.y/O,2.);
    vec4 nana = texture(iChannel0,Q.xy/R*vec2(.3,1.)-vec2(.38,-.1));
    Q.z=nana.w;
    if (Q.z<1e-4) Q.xy = -1e9*R;
    Q.xy*=0.7;
    Q.w = floor(nana.x*1e2)*1e4+floor(nana.y*1e2)*1e2+floor(nana.z*1e2);
}

#define R iResolution.xy
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texelFetch(iChannel1,ivec2(U),0)
#define C(U) texelFetch(iChannel2,ivec2(U),0)
#define D(U) texelFetch(iChannel3,ivec2(U),0)
#define Main void mainImage (out vec4 Q, vec2 U) 
#define Init if (iFrame < 1) 
#define Border if (U.x<1.||R.x-U.x<1.||U.y<1.||R.y-U.y<1.)
#define O 6.
#define shape(U) (round((U)/O)*O)
#define N 12
#define Z vec2(u)
float angle (vec2 a, vec2 b) {
	return atan(a.x*b.y-a.y*b.x,dot(a.xy,b.xy));
}
float sg (vec2 p, vec2 a, vec2 b) {
    if (length(a-b)<1e-4||length(a)<1e-4||length(b)<1e-4) return 1e9;

    float i = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
	float l = (length(p-a-(b-a)*i));
    return l;
}

float pie (vec2 p, vec2 a, vec2 b) {
	vec2 m = 0.5*(a+b); // midpoint
    if (length(a-b)<1e-3) return 1e3; // ignore self
	return abs(dot(p-m,b-m)/dot(b-m,b-m)); // pojection
} 
float line (vec2 p, vec2 a, vec2 b) {
    if (length(a-b)<1.||length(a)<1.||length(b)<1.) return 1e9;

    float i = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
	float l = (length(p-a-(b-a)*i));
    l=(.1+l)*(pie(p,a,b));
    return l;
}
