float hash12(vec2 p){
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);}

void mainImage( out vec4 f, in vec2 u ){
    vec2 uv = vec2(u.xy - .5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(1);

    uv.x+=iTime*.2;
	vec2 fuv = fract(uv*6.);
    vec2 id = floor(uv*6.);

    float chk = mod(id.y+id.x,2.);
    float c = hash12(id);

    col = mix(vec3(.4, .1, .1), vec3(.4, .9, .9), 
              mod(id.x+id.y,2.));

    col*=mix(vec3(1., 0., .5), vec3(0.,.6, .2), c);

    if(chk > 0.){
     	col -= (sin(fuv.x*76.+.5))*.5;	
        col *= smoothstep(.6, 0.,abs(fuv.y-0.5));
    }
    else{
       col -= (sin(fuv.y*76.))*.5;
       col *= smoothstep(.6, 0.,abs(fuv.x-0.5));
    }
    f = vec4(col, 1.)*.8;

}
