#define STRENGTH 0.6

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0, 0.0, 0.0, 0.0);

	float single_pixel_dist = 1.0 / iResolution.y;

	float aggregate_alpha = 0.0f;

	float div = 0.0f;

	for (int i = -2; i < 3; i++) {

        float diff = 1.0 / (abs(float(i)) + 1.0);
		int line = (
            (
               int(
                   ( 
                       (fragCoord.y / iResolution.y) 
                		+ single_pixel_dist * float(i) 
                		+ 0.5 / iResolution.y) 
            		* 320.0 )
            	) % 2
        	);

		if (line == 1) {
			aggregate_alpha += STRENGTH * diff;
		}

        div = div + diff;

	}

	fragColor.g = aggregate_alpha / div;


}
