void mainImage( out vec4 Q, in vec2 U )
{
    vec4 a = A(U),
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
    float c = 0.5*(n.x-s.x+e.y-w.y);
    Q = 0.5+0.5*vec4(c)+.1*a;
}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define M 0.25*(A(U+vec2(0,1))+A(U-vec2(0,1))+A(U+vec2(1,0))+A(U-vec2(1,0)))
vec2 sq(vec2 v) { return vec2(v.x*v.x-v.y*v.y,2.*v.x*v.y);}
vec2 e(vec2 v) { return exp(v.x)*vec2(cos(v.y),sin(v.y));}
mat2 r(float a) {float s = sin(a), c=cos(a);return mat2(c,-s,s,c);}
#define F .5*(m.xy-Q.xy*length(Q.xy))
#define O 1e-4

void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    vec4 m = M;
    Q.x -= (F).y;

    Q.xy *= r(O*length(U-0.5*R));
    if (iFrame < 1||(iMouse.z>0.&&length(U-iMouse.xy)<10.)) Q.xy = vec2(sin(iTime),cos(iTime));
	if (U.x<1.) Q.xy = vec2(1,0);
	if (R.x-U.x<1.) Q.xy = vec2(-1,0);
   	if (U.y<1.) Q.xy = vec2(0,1);
	if (R.y-U.y<1.) Q.xy = vec2(0,-1);
}

void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    vec4 m = M;
    Q.y += (F).x;

    Q.xy *= r(O*length(U-0.5*R));
    if (iFrame < 1||(iMouse.z>0.&&length(U-iMouse.xy)<10.)) Q.xy = vec2(sin(iTime),cos(iTime));
