float ease( float x ) {                                 // rewritten from https://shadertoy.com/view/3ddBWB
	return x > .5 ? x = 1.-x, 1. - 4.* x*x*x            // stepier than smoothstep or .5-.5*cos
                  :                4.* x*x*x ;
}

#define B(x)  mod( floor(x), 2. )

void mainImage( out vec4 O, vec2 u )
{   
    O-=O; 
    vec2 R = iResolution.xy,
         U = u *= 8. / R.y;
    if (U.x > 8.) { O += .5; return; }                  // out of board

    float T = fract(iTime/2.), t = ease( T ), f,
          n = B(iTime/2.);

    if ( n > 0. ) U.x += 1.+t, T=0.;                    // phase 2: translate

    U = B(U);  

    u = abs( fract(4.*u) *2. - 1. );                    // phase 1: fragmentation
    f = smoothstep(  48./R.y, 0., max(u.x,u.y) - 1.+smoothstep(0.,1.,T) );

    O += U.x == U.y ? 1.-f : f;                         // checker cell color XOR fragmentation   
}
