// USING YX/LUNA'S AWESOME DITHERED MOTION BLUR - https://www.shadertoy.com/view/wsfcWX
// I modified it to not be framerate dependent like the original.



#define pi acos(-1.)
#define tau (2.*pi)
#define rot(x) mat2(cos(x),-sin(x),sin(x),cos(x))

#define timeStep (1./60.)
#define motionBlurSteps 10


float bayer8(ivec2 uv)
{   
    uv %= 8;
	return texelFetch(iChannel1,uv,0).r;
}


// from https://necessarydisorder.wordpress.com/
float ease(float p, float g) {
    if(p < 0.){
        return 0.;
    } else if(p > 1.){
    	return 1.;
    } else {
        if (p < 0.5) 
            return 0.5 * pow(2.*p, g);
        else
            return 1. - 0.5 * pow(2.*(1. - p), g);
    }
}

float ease(float p, float power, float jump, float offs, float end) {
    float j = p;
    p *= offs;
    float r = (1.-cos(pi*p))/2.*jump;
    r = pow(r, power);
    r = mix(r, 1., pow(smoothstep(0.,1.,j),end));
	return r;
}
float smease(float p, float g) {
    float s = p;
    for(float i = 0.; i < g; i++){
    	s = smoothstep(0.,1.,s);
    }
    return s;
}


float sdBox(vec2 p, vec2 s){
	p = abs(p) - s;
	return max(p.x, p.y);
}
float sdCirc(vec2 p, float s){
    return length(p) - s;
}

float sdEquilateralTriangle(  vec2 p, float s )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - s;
    p.y = p.y + s/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0, 0.0 );
    float d = -length(p)*sign(p.y);

    return d;
}
const float speed = 0.28;
float[] scenes = float[4](3.2*speed,1.7*speed, 2.6*speed, 1.2*speed); 

const float S = 1.;

vec3 get( vec2 uv, float t  )
{
 vec2 suv = uv;   
    //uv += S*0.05;
    uv *= 2.;

    uv.y += S*0.25;
    float col = 0.;

    float d = 10e6;

    float bA;
    float bB;
    float bC;
    float bD;
    float bE;

    float pl;


    //col += smoothstep(0.5,0.1, abs(uv.y));

    //t += scenes[0];
    //t += scenes[1] - 0.1;
    //t += scenes[2] - 0.1;
    t = mod(t, scenes[0] + scenes[1] + scenes[2] + scenes[3]  );


    float iters = 45.;

    float sep = 5.;
    float pp = 1.645;

    #define sc(x) vec2(sin((x)*tau), cos((x)*tau))

    if(t < scenes[0]){
        float lsp = 1.2;
        float env = ease(t/scenes[0]*lsp, 3.);  

        for(float i = 0.; i < iters + min(float(iFrame),0.); i++){
            vec2 suv = uv;
            if(i != iters - 1.){
            	suv *= rot((iters - i)*1.65*env/pi);
            }
            float dTriA = sdEquilateralTriangle(  suv, S/exp((iters - i)/sep) );

            d = max(d, dTriA);
        	d = min(d, abs(dTriA) - dFdx(uv.x)/2.);

        }
    } else if(t < scenes[0] + scenes[1]){
        t -= scenes[0];
        float lsp = 1.2;
        float env = ease(t/scenes[1]*lsp, 3.);  


        for(float i = 0.; i < iters + min(float(iFrame),0.); i++){
            vec2 suv = uv;
            if(i != iters - 1.){
            	suv *= rot((iters - i)*pp/pi + (iters - i)*pp/pi*env);
            }
            float dTriA = sdEquilateralTriangle(  suv, S/exp((iters - i)/sep) );

            d = max(d, dTriA);
        	d = min(d, abs(dTriA) - dFdx(uv.x)/2.);

        }
    } else if(t < scenes[0] + scenes[1] + scenes[2]){
        t -= scenes[0] + scenes[1];
        float lsp = 1.2;
        float env = ease(t/scenes[2]*lsp, 3.);  

        for(float i = 0.; i < iters + min(float(iFrame),0.); i++){
            vec2 suv = uv;
            if(i != iters - 1.){
            	suv *= rot((iters - i)*pp/pi + (iters - i)*pp/pi + (iters - i)*pp/pi*env*2. );
            }
            float dTriA = sdEquilateralTriangle(  suv, S/exp((iters - i)/sep) );

            d = max(d, dTriA);
        	d = min(d, abs(dTriA) - dFdx(uv.x)/2.);

        }
    }else if(t < scenes[0] + scenes[1] + scenes[2] + scenes[3]){
        t -= scenes[0] + scenes[1] + scenes[2];
        float lsp = 1.2;
        float env = ease(t/scenes[3]*lsp, 3.)*smoothstep(1.,0.,t/scenes[3]*lsp)*0.05;  

        for(float i = 0.; i < iters + min(float(iFrame),0.); i++){
            vec2 suv = uv;
            if(i != iters - 1.){
            }
            float dTriA = sdEquilateralTriangle(  suv, (S + env)/exp((iters - i )/(sep - env*1. )) );

            d = max(d, dTriA);
        	d = min(d, abs(dTriA) - dFdx(uv.x)/2.);

        }
    }

    //d = min(d, abs(suv.y) - 0.001);
    //d = min(d, abs(suv.x) - 0.001);
    return vec3(d);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;  
    vec3 col = vec3(0);

    float edge = dFdx(uv.x)*1.;

    for (int i = 0; i < motionBlurSteps  ; ++i) {
        float subsample = bayer8(ivec2(fragCoord));
        float time = iTime + ((float(i)+subsample)/float(motionBlurSteps)-.5) * timeStep;
        col += smoothstep(-edge,edge,-get(uv,time));
    }
    col/=float(motionBlurSteps);

    col = clamp(col, 0., 1.);
    col *= 0.999;

    //col *= max((1. + 1.*cos(sin(0.25*tau*iTime/(scenes[0] + scenes[1] ))*tau+length(uv)*2.+vec3(4,2.5,1.5)))*1., 0.);
    //col *= max((1.5 + 1.4*cos(iTime+length(uv)*2.+vec3(1,1.5,1.5)))*1., 0.);

    col *= 1. - pow(smoothstep(0.,1.,dot(uv,uv)*0.9),0.9)*1.;

    col = pow(col, vec3(0.454545));

    fragColor = vec4(col,1.0);
}
