vec4 grad(ivec2 p)
{
    ivec4 sp = get(p);
    ivec4 spx = get(p+ivec2(1,0));
    ivec4 spy = get(p+ivec2(0,1));
    return tanh(100.*(abs(vec4(spx - sp)) + abs(vec4(spy - sp))));
}

