void mainImage( out vec4 U, in vec2 pos )
{
    ivec2 p = ivec2(pos);
    if(p.x < N.x && p.y < N.y)
    {
        pos = floor(pos);
        //this pixel value
        U = texel(ch0, pos);

        vec2 F = vec2(0.);
        if(iMouse.z > 0.) 
        {
            float d = distance(iMouse.xy, U.xy);
            F = normalize(iMouse.xy - U.xy)/(d+2.);
        }

        U.zw += dt*F;
        U.xy += U.zw*dt;

        if(size.x - U.x < 2.) U.z = -abs(U.z);
        if(U.x < 2.) U.z = abs(U.z);
        if(size.y - U.y < 2.) U.w = -abs(U.w);
        if(U.y < 2.) U.w = abs(U.w);

        //this pixel value
        if(iFrame<1)
        {
            U.xy = size*hash22(pos);
			U.zw = 2.*(hash22(3.14159*pos) - 0.5);
        }
    }
    else discard;
}

