void sort(int utemp, ivec2 p)
{
    vec2 pos = vec2(p);
   	float dtemp = particleDistance(utemp, pos);
    //sorting
    if(d.x > dtemp)
    {
        //insert
        d = vec4(dtemp, d.xyz);
        u = ivec4(utemp, u.xyz);
    }
    else if(d.y > dtemp && dtemp > d.x)
    {
        //insert
        d = vec4(d.x, dtemp, d.yz);
        u = ivec4(u.x, utemp, u.yz);
    }
    else if(d.z > dtemp && dtemp > d.y)
    {
        //insert
        d = vec4(d.xy, dtemp, d.z);
        u = ivec4(u.xy, utemp, u.z);
    }
    else if(d.w > dtemp && dtemp > d.z)
    {
        //insert
        d = vec4(d.xyz, dtemp);
        u = ivec4(u.xyz, utemp);
    }
}

void upd(ivec2 p, ivec2 dx)
{
    ivec4 t = get(p+dx);
    sort(t.x, p);
    sort(t.y, p);
    sort(t.z, p);
    sort(t.w, p);
}

void randupd(ivec2 p)
{
    for(int i = 0; i < 4; i++)
    {
    	//go through the entire array sudorandomly
    	int t = ((iFrame + p.x + p.y*int(iResolution.x))^(1<<(3*i)))%tot_n;
    	sort(t, p);    
    }
}

void updrad(ivec2 p, int R)
{
    int r = 1<<R;
    upd(p, ivec2(r, 0));
    upd(p, ivec2(-r, 0));
    upd(p, ivec2(0, r));
    upd(p, ivec2(0, -r));
}

void update(ivec2 p)
{
    randupd(p);
    upd(p, ivec2(0));
    updrad(p, 0);
    updrad(p, 3);
}

void mainImage( out vec4 U, in vec2 pos )
{
    ivec2 p = ivec2(pos);

    //particle indecies 
    u = ivec4(1);
    d = vec4(1e10); 

    update(p);

    U = save(u);
}
