void mainImage( out vec4 O, vec2 u )
{
    vec2 res = iResolution.xy,
          uv = ( u* 2. - res) / min(res.x, res.y);

    float t = iTime;
    float scale = 20.0;

    float x = uv.x* scale;
    float y = uv.y* scale;

    float a = 1.* sin(0.5* t);
    float b = 10.* sin(1.2* t);
    float c = 8.* sin(1.3* t);
    float d = 10.* sin(1.4* t);
    float e = 100.* sin(1.* t);
    float f = -1.* sin(0.78* t);
    float g = 12.* sin(1.7* t);
    float h = 6.* sin(1.7* t);
    float i = 6.* sin(1.7* t);

    float p = a* x* x* x* x + b* x* x *x + c* x* x + d* x + e
        + tan(x)* tan(x)* tan(x)* tan(x)* tan(x);
    float q = f* y* y* y* y + g* y* y* y + h* y* y + i* y
        + tan(y)* tan(y)* tan(y)* tan(y)* tan(y);

    O = vec4(vec3(1. / pow(p - q, .15)), 1.);
}
