// object transformation
vec3 rotate_x(vec3 p, float phi){
    float c = cos(phi);	float s = sin(phi);
	return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}

vec3 rotate_y(vec3 p, float phi){
	float c = cos(phi);
	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}

vec3 rotate_z(vec3 p, float phi){
	float c = cos(phi);
	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}

vec2 rotatev2(vec2 p, float ang){
    float c = cos(ang);
    float s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}

vec3 BocaSonrisa(vec3 p){
 	vec3 res = vec3(9999.0, -1.0,-1.0);
    float sdcy1= sdCylinderXY(p-vec3(-0.7,-1.5,0.0), vec2(3.0,3.0) );
    float sdcy2= sdCylinderXY(p-vec3(-0.7,-1.5,0.0), vec2(2.0,3.5) );
    float sdb1= sdBox(p-vec3(0.0,1.0,0.0), vec3(4.2,3.0,3.6));   
    float dif; 
    dif= differenceSDF(sdcy1, sdcy2); 
    dif= differenceSDF(dif, sdb1); 
    res =opU3(res, vec3(dif,3.0,MATERIAL_NO));        
    return res;
}

