float planodeforme(vec3 pp){
    vec3 p= pp;
    float h = dot(sin(p - cos(p.yzx*1.5)), vec3(.3));
    h += dot(sin(p*3. - cos(p.yxz*3.3)), vec3(0.2/3.));
    return p.y + 1. + h;     
}
