float hex(vec2 p, float r2)
{
    p.x *= 1.16;
    p.y += mod(floor(p.x), 4.0) * 0.5;
    p = abs((mod(p, 1.00) - 0.5));
    return abs(max(p.x * 1.5 + p.y, p.y * 2.0) - r2);
}

vec4 hexColor(vec2 pos)
{
    vec2  p   = pos * 0.2;
    float r1  = 0.25;
    float r2  = -0.005;
    float hexv=hex(p,1.0 - r2);

    vec4 hColor = vec4(smoothstep(.0, r1, hexv));
    hColor = (1.0 - hColor) * 0.65;
    return mix(hColor,vec4(0.35,0.53,1.0,1.0),hexv);
}
