#define TAU 6.283185307179586

vec3 dark = vec3(0.27);
vec3 mid = vec3(0.41);
vec3 light = vec3(0.62);

vec4 sine(vec2 k, float w, float t, vec2 p) {
    float y = k.y * sin(k.x*p.x);
    vec3 c = fract(k.x*(p.x+t)/TAU) < 0.5 ? light : dark;
    return y - 0.5*w <= p.y && p.y <= y + 0.5*w ? vec4(c, 1.0) : vec4(0.0);
}

float map(float x, float l0, float r0, float l1, float r1) {
	return (x - l0) / (r0 - l0) * (r1 - l1) + l1;
}

float imod(float x, float l, float r) {
	return map(fract(map(x, l, r, 0.0, 1.0)), 0.0, 1.0, l, r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord/iResolution.xy*2.0 - 1.0)*vec2(1.0, iResolution.y/iResolution.x);
   	p = fragCoord * 0.005;

    float kt = 0.01*iTime;
    vec2 sine_k = vec2(32.0, 0.013);
    float dy = 0.1;
    float w = 0.01;
    float r = sine_k.y+w;
    float l = -r;
    float df = map(p.y + dy, l, r, 0.0, 1.0/3.0);
    float d = floor(df);
    float dir = mod(d, 2.0) < 1.0 ? 1.0 : -1.0;
    float o = d * TAU * (dir >= 0.0 ? 0.5 : 0.0) / sine_k.x;
    vec2 kp = vec2(kt * dir, dy);
    vec2 p1 = p + kp;
    p1.x += o;
    p1.y = imod(p1.y, l, r); 

    vec3 bg = mid;
    vec3 color = bg;
    vec4 sine_color = sine(sine_k, w, -kt * dir + (dir >= 0.0 ? 0.5*TAU/sine_k.x : 0.0), p1);
    color = mix(color, sine_color.rgb, sine_color.a);     

    fragColor = vec4(color,1.0);
}
