vec3 GetDist(vec3 p  ) 
{	
	vec3 res;
    vec3 pp=p;
    res = vec3(9999.0, -1.0,-1.0);
   float planeDist1 = p.y+0.0;  //piso inf

    vec3 posA=p;
    posA.z= opRep1D(posA.z, 60.0 );
    posA.x= opRep1D(posA.x, 60.0 );
    vec3 prepLinea1=posA;

    prepLinea1.z= opRep1D( prepLinea1.z, 10.0 );


    //hacer calle A
    float sdr1A= sdRoundBox(posA-vec3(0.0), vec3(10.0,1.0,20.0), 0.2 );
    float sdr1B= sdRoundBox(posA-vec3(-12.0,0.0,0.0), vec3(2.0,1.5,20.0), 0.2 );
    float sdr1C= sdRoundBox(posA-vec3(12.0,0.0,0.0), vec3(2.0,1.5,20.0), 0.2 );
    float sdrLinea1= sdRoundBox(prepLinea1-vec3(.0,0.0,0.0), vec3(0.05,1.1,1.0), 0.2 );



    //hacer calle B
    vec3 posB=p-vec3(-30.0,0.0,30.0);
    posB.z= opRep1D(posB.z, 60.0 );
    posB.x= opRep1D(posB.x, 60.0 );
    posB= rotate_y(posB, radians(90.0));
    vec3 prepLinea2=posB;

    prepLinea2.z= opRep1D( prepLinea2.z, 10.0 );



    float sdr2A= sdRoundBox(posB-vec3(0.0), vec3(10.0,1.0,20.0), 0.2 );
    float sdr2B= sdRoundBox(posB-vec3(-12.0,0.0,0.0), vec3(2.0,1.5,20.0), 0.2 );
    float sdr2C= sdRoundBox(posB-vec3(12.0,0.0,0.0), vec3(2.0,1.5,20.0), 0.2 );
    float sdrLinea2= sdRoundBox(prepLinea2-vec3(.0,0.0,0.0), vec3(0.05,1.1,1.0), 0.2 );

    //calle centro
    vec3 posC=p-vec3(0.0,0.0,30.0);
     posC.z= opRep1D(posC.z, 60.0 );
     posC.x= opRep1D(posC.x, 60.0 );

    float sdr3A= sdRoundBox(posC-vec3(0.0), vec3(10.0,1.0,10.0), 0.2 );

     //jardin
   vec3 posD=p-vec3(-31.0,0.0,-13.0);
     posD.z= opRep1D(posD.z, 60.0 );
     posD.x= opRep1D(posD.x, 62.0 );

   float sdr4A= sdRoundBox(posD-vec3(0.0,0.0,12.), vec3(17.0,18.0+22.0*sin(iTime),15.0), 0.2 );


      //calle a
    res =opU3(res, vec3(sdr1A,-1.0,4.0)); 
    res =opU3(res, vec3(sdr1B,8.0,-1.0)); 
    res =opU3(res, vec3(sdr1C,8.0,-1.0)); 
    res =opU3(res, vec3(sdrLinea1,2.0,-1.0)); 


    // calle b
    res =opU3(res, vec3(sdr2A,-1.0,4.0)); 
    res =opU3(res, vec3(sdr2B,8.0,-1.0)); 
    res =opU3(res, vec3(sdr2C,8.0,-1.0)); 
    res =opU3(res, vec3(sdrLinea2,2.0,-1.0)); 

    //calle c
    res =opU3(res, vec3(sdr3A,-1.0,4.0)); 
    res =opU3(res, vec3(sdr4A,-1.0,2.0)); 

    res =opU3(res, vec3(planeDist1,-1.0,5.0)); 

    //res =opU3(res, vec3(1.0,0.0,MATERIAL_NO)); 
    //return (dist, id_color, id_material)
    return res;
}


vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro) 
{
    vec3 l = lp - p;
    float dist = max(length(l), 0.01);
    float atten = min(1./(1. + dist*0.5), 0.2);
    l /= dist;

    vec3 n = normal;
   	vec3 r = reflect(-l, n);

    float dif = clamp(dot(l, n), 0.0, 1.0);
    float spe = pow(clamp(dot(r, -rd), 0.0, 1.0), 8.0);
    float fre = pow(clamp(1.0 + dot(n, rd), 0.0, 1.0), 2.0);
    float dom = smoothstep(-1.0, 1.0, r.y);

    vec3 lin = vec3(0.2);
    lin += 1.0*dif*vec3(1, .97, .85);
    lin += 2.5*spe*vec3(1, .97, .85)*dif;
    lin += 2.5*fre*vec3(1);
    lin += 0.5*dom*vec3(1);

    return lin*atten;
}



