// "Truchet Balls" by dr2 - 2020
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

/*
  Balls in and between channels based on hexagonal Truchet tiles 
  (mouseable, click upper-right for new run, lower-right to
  switch between distant viewpoint and panoramic view near random ball)
  In addition to gravity and friction there is a horizontal force that varies
  with time, and other forces that bias the movement and reduce dispersal.
*/

mat3 QtToRMat (vec4 q);
vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
float Minv3 (vec3 p);
vec2 Rot2D (vec2 q, float a);
vec3 HsvToRgb (vec3 c);
float Hashfv2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
vec4 Loadv4 (int idVar);

const int nBall = 64;
vec3 vnObj, ltDir;
vec2 gId, cMid;
float tCur, dstFar, hgSize, cDir, trWidf, trHt;
int nFrame, idBall;
const float pi = 3.14159, sqrt3 = 1.73205;

#define VAR_ZERO min (nFrame, 0)

float SurfHt (vec2 p)
{
  vec3 w;
  vec2 s, r;
  float b;
  p -= cMid;
  r = p / hgSize;
  s = vec2 (0., - cDir);
  b = dot (s - r, s - r);
  w = vec3 (s, b);
  s = 0.5 * vec2 (sqrt3, cDir);
  b = dot (s - r, s - r);
  if (b < w.z) w = vec3 (s, b);
  s = 0.5 * vec2 (- sqrt3, cDir);
  b = dot (s - r, s - r);
  if (b < w.z) w = vec3 (s, b);
  return trHt * smoothstep (0.25 * trWidf, trWidf, abs (sqrt (w.z) - 0.5));
}

float SurfDf (vec3 p)
{
  return 0.8 * (p.y - SurfHt (p.xz));
}

void SetTrConf ()
{
  cMid = HexToPix (gId * hgSize);
  cDir = 2. * step (Hashfv2 (gId), 0.5) - 1.;
}

float SurfRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, gIdP;
  float dHit, d, s, eps;
  eps = 0.0005;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = hgSize / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]),
     dot (ro.xz, edN[2])) / hgSize;
  pM = HexToPix (PixToHex (ro.xz / hgSize));
  gIdP = vec2 (-99.);
  dHit = 0.;
  for (int j = VAR_ZERO; j < 220; j ++) {
    hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
    s = Minv3 (hv);
    p = ro + dHit * rd;
    gId = PixToHex (p.xz / hgSize);
    if (gId.x != gIdP.x || gId.y != gIdP.y) {
      gIdP = gId;
      SetTrConf ();
    }
    d = SurfDf (p);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
    }
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 SurfNf (vec3 p)
{
  const vec2 e = vec2 (0.01, 0.);
  return normalize (vec3 (SurfHt (p.xz) - vec2 (SurfHt (p.xz + e.xy), SurfHt (p.xz + e.yx)), e.x).xzy);
}

float BallHit (vec3 ro, vec3 rd)
{
  vec4 p;
  vec3 u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (4 * n);
    u = ro - p.xyz;
    rad = 0.45;
    b = dot (rd, u);
    w = b * b - dot (u, u) + rad * rad;
    if (w >= 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) {
        dMin = d;
        vnObj = (u + d * rd) / rad;
        idBall = n;
      }
    }
  }
  return dMin;
}

float BallHitSh (vec3 ro, vec3 rd, float rng)
{
  vec4 p;
  vec3 rs, u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (4 * n);
    u = ro - p.xyz;
    rad = 0.45;
    b = dot (rd, u);
    w = b * b - dot (u, u) + rad * rad;
    if (w > 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) dMin = d;
    }
  }
  return smoothstep (0., rng, dMin);
}

float BallChqr (int idBall, vec3 vnBall)
{
  vec3 u;
  vec2 a;
  u = vnBall * QtToRMat (Loadv4 (4 * idBall + 2));
  a = mod (floor (8. * vec2 (atan (u.x, u.y), asin (u.z)) / pi), 2.) - 0.5;
  return 0.7 + 0.3 * step (0., sign (a.x) * sign (a.y));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, bgCol;
  float dstObj, dstSurf, sh;
  bgCol = mix (vec3 (0.8, 0.8, 0.6), vec3 (0.7, 0.7, 1.), smoothstep (-0.1, 0.05, rd.y));
  dstObj = BallHit (ro, rd);
  dstSurf = SurfRay (ro, rd);
  if (min (dstObj, dstSurf) < dstFar) {
    if (dstObj < dstSurf) {
      ro += dstObj * rd;
      vn = vnObj;
      col4 = vec4 (HsvToRgb (vec3 (float (idBall) / float (nBall), 0.8, BallChqr (idBall, vn))), 0.2);
    } else if (dstSurf < dstFar) {
      ro += dstSurf * rd;
      col4 = mix (vec4 (0.5, 0.35, 0.25, 0.), vec4 (1., 1., 0.7, 0.), smoothstep (0.94, 0.98, ro.y / trHt));
      vn = VaryNf (2. * ro, SurfNf (ro), max (0.02, 2. - 2. * dstSurf / dstFar));
    }
    sh = 0.5 + 0.5 * BallHitSh (ro + 0.005 * ltDir, ltDir, 5.);
    col = col4.rgb * (0.1 + 0.2 * max (- dot (vn, ltDir), 0.) +
       0.8 * sh * max (dot (vn, ltDir), 0.)) +
       col4.a * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
    col = mix (col, bgCol, smoothstep (0.7, 1., min (dstObj, dstSurf) / dstFar));

  } else col = bgCol;
  return clamp (col, 0., 1.);
}

#define AA    1

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat, mPtr;
  vec3 col, rd, ro, vd, bMid;
  vec2 canvas, uv, uvv, ut;
  float az, el, asp, zmFac, sr, s, mb, bMid2;
  int fBall;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  nFrame = iFrame;
  asp = canvas.x / canvas.y;
  stDat = Loadv4 (4 * nBall + 0);
  hgSize = stDat.x;
  trWidf = stDat.y;
  trHt = stDat.z;
  stDat = Loadv4 (4 * nBall + 1);
  mPtr.xyz = stDat.xyz;
  fBall = int (stDat.w);
  ut = vec2 (mPtr.x, abs (mPtr.y)) + 0.05 * vec2 (1. / asp, 1.) - 0.5;
  mb = min (ut.x, ut.y);
  if (mPtr.z > 0. && mb < 0.) {
    az = 2. * pi * mPtr.x;
    el = 0.5 * pi * (mPtr.y + 0.25);
    el = clamp (el, 0.02 * pi, 0.3 * pi);
  } else if (fBall < 0) {
    az -= 0.02 * pi * tCur;
    el = 0.06 * pi;
  } else {
    az = 0.;
    el = 0.;
  }
  ut = uv;
  if (fBall < 0) {
    stDat = Loadv4 (4 * nBall + 2);
    bMid = stDat.xyz;
    bMid2 = stDat.w;
    ro = bMid + min (bMid2, 80.) * vec3 (cos (el) * sin (az + vec2 (0.5 * pi, 0.)), 2. * sin (el)).xzy;
    vd = normalize (bMid - ro);
    s = sqrt (max (1. - vd.y * vd.y, 1e-6));
    vuMat = mat3 (vec3 (vd.z, 0., - vd.x) / s, vec3 (- vd.y * vd.x, 1. - vd.y * vd.y,
       - vd.y * vd.z) / s, vd);
    zmFac = 4.;
  } else {
    ro = Loadv4 (4 * fBall + 0).xyz;
    ro.y += 1.;
    vd = Loadv4 (4 * fBall + 1).xyz;
    vd = normalize (vec3 (vd.x, 0., vd.z));
    vd.xz = Rot2D (vd.xz, - az);
    ro.xz -= 0.3 * vd.xz;
    vuMat = mat3 (vec3 (vd.z, 0., - vd.x), vec3 (0., 1., 0.), vd);
  }
  dstFar = 200.;
  ltDir = normalize (vec3 (0., 1., -1.));
  ltDir.xz = Rot2D (ltDir.xz, 0.04 * pi * tCur);
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) * pi);
    rd = (fBall < 0) ? vec3 (uvv, zmFac) : vec3 (0.5 * sin (uvv.x * pi / asp + vec2 (0., 0.5 * pi)), uvv.y).xzy;
    rd = vuMat * normalize (rd);
    col += (1. / naa) * ShowScene (ro, rd);
  }
  if (mPtr.z > 0. && min (uv.x - asp, abs (uv.y) - 1.) > -0.1)
     col = mix (col, vec3 (0., 0.3, 1.), 0.3);
  fragColor = vec4 (pow (col, vec3 (0.9)), 1.);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e;
  e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

// "Truchet Balls" by dr2 - 2020
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

vec4 QtMul (vec4 q1, vec4 q2);
vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
vec4 RMatToQt (mat3 m);
mat3 LpStepMat (vec3 a);
float Hashff (float p);
float Hashfv2 (vec2 p);
vec3 Hashv3f (float p);
vec4 Loadv4 (int idVar);
void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord);

const int nBall = 64;
float todCur, nStep, dstFar, hgSize, trWidf, trHt;
int nFrame, fBall;
const float pi = 3.14159, sqrt3 = 1.73205;
const float txRow = 128.;

#define VAR_ZERO min (nFrame, 0)

float SurfHt (vec2 p)
{
  vec3 w;
  vec2 gId, cMid, s, r;
  float cDir, b;
  gId = PixToHex (p / hgSize);
  cMid = HexToPix (gId * hgSize);
  cDir = 2. * step (Hashfv2 (gId), 0.5) - 1.;
  p -= cMid;
  r = p / hgSize;
  s = vec2 (0., - cDir);
  b = dot (s - r, s - r);
  w = vec3 (s, b);
  s = 0.5 * vec2 (sqrt3, cDir);
  b = dot (s - r, s - r);
  if (b < w.z) w = vec3 (s, b);
  s = 0.5 * vec2 (- sqrt3, cDir);
  b = dot (s - r, s - r);
  if (b < w.z) w = vec3 (s, b);
  return trHt * smoothstep (0.25 * trWidf, trWidf, abs (sqrt (w.z) - 0.5));
}

vec3 SurfNf (vec3 p)
{
  const vec2 e = vec2 (0.01, 0.);
  return normalize (vec3 (SurfHt (p.xz) - vec2 (SurfHt (p.xz + e.xy), SurfHt (p.xz + e.yx)), e.x).xzy);
}

void Step (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  vec4 p;
  vec3 rmN, rmN1, rmN2, vmN, wmN, dr, dr1, dr2, dv, drw, am, wam;
  float fOvlap, fricN, fricT, fricS, fricSW, fDamp, fPull, fCent, grav, rSep,
     fc, ft, drv, dt, spFreq;
  fOvlap = 500.;
  fricN = 0.5;
  fricS = 0.1;
  fricSW = 0.5;
  fricT = 0.5;
  fPull = 0.8;
  fCent = 0.01;
  spFreq = 0.0002;
  fDamp = 0.4;
  grav = 6.;
  p = Loadv4 (4 * mId);
  rm = p.xyz;
  vm = Loadv4 (4 * mId + 1).xyz;
  qm = Loadv4 (4 * mId + 2);
  wm = Loadv4 (4 * mId + 3).xyz;
  am = vec3 (0.);
  wam = vec3 (0.);
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (4 * n);
    rmN = p.xyz;
    dr = rm - rmN;
    rSep = length (dr);
    if (n != mId && rSep < 1.) {
      fc = fOvlap * (1. / rSep - 1.);
      vmN = Loadv4 (4 * n + 1).xyz;
      wmN = Loadv4 (4 * n + 3).xyz;
      dv = vm - vmN;
      drv = dot (dr, dv) / (rSep * rSep);
      fc = max (fc - fricN * drv, 0.);
      am += fc * dr;
      dv -= drv * dr + cross (0.5 * (wm + wmN), dr);
      ft = min (fricT, fricS * abs (fc) * rSep / max (0.001, length (dv)));
      am -= ft * dv;
      wam += (ft / rSep) * cross (dr, dv);
    }
  }
  dr.xz = 0.5 * SurfNf (rm).xz;
  dr.y = rm.y + 0.5 - SurfHt (rm.xz - dr.xz);
  rSep = length (dr);
  if (rSep < 1.) {
    fc = fOvlap * (1. / rSep - 1.);
    dv = vm;
    drv = dot (dr, dv) / (rSep * rSep);
    fc = max (fc - fricN * drv, 0.);
    am += fc * dr;
    dv -= drv * dr + cross (wm, dr);
    ft = min (fricT, fricSW * abs (fc) * rSep / max (0.001, length (dv)));
    am -= ft * dv;
    wam += (ft / rSep) * cross (dr, dv);
  }
  am.xz += fPull * sin (spFreq * 2. * pi * nStep + vec2 (0.5 * pi, 0.));
  am.z += 1.5 * fPull;
  am.x -= fCent * rm.x;
  am.y -= grav;
  am -= fDamp * vec3 (1., 5., 1.) * vm;
  wam -= 1e-4 * fDamp * wm;
  dt = (fBall < 0) ? 0.02 : 0.005;
  vm += dt * am;
  rm += dt * vm;
  wm += dt * wam / 0.1;
  qm = normalize (QtMul (RMatToQt (LpStepMat (0.5 * dt * wm)), qm));
}

void Init (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  float mIdf;
  mIdf = float (mId);
  rm.xz = vec2 (0., 3. * mIdf) + 35. * mod (todCur, 10.);
  rm.y = 1.5;
  vm = 0.5 * (2. * Hashv3f (mIdf + todCur) - 1.);
  qm = vec4 (0., 0., 0., 1.);
  wm = vec3 (0.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr, mPtrP, stDat, qm;
  vec3 rm, vm, wm, bMid;
  vec2 iFrag, canvas, b;
  float asp, bMid2;
  int mId, pxId, kp;
  bool doInit;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (iFrag.x >= txRow || pxId >= 4 * nBall + 3) discard;
  canvas = iResolution.xy;
  todCur = iDate.w;
  nFrame = iFrame;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  mId = (pxId < 4 * nBall) ? pxId / 4 : -1;
  fBall = -1;
  if (nFrame <= 5) {
    hgSize = 4.;
    trWidf = 0.25;
    trHt = 0.4;
    doInit = true;
  } else {
    doInit = false;
    stDat = Loadv4 (4 * nBall);
    hgSize = stDat.x;
    trWidf = stDat.y;
    trHt = stDat.z;
    nStep = stDat.w;
    ++ nStep;
    stDat = Loadv4 (4 * nBall + 1);
    mPtrP.z = stDat.z;
    fBall = int (stDat.w);
    if (mPtr.z > 0. && mPtrP.z < 0. && mPtr.x > 0.5 - 0.05 / asp && abs (mPtr.y) > 0.5 - 0.05) {
      if (mPtr.y > 0.) {
        if (nStep > 100.) doInit = true;
      } else {
        if (fBall >= 0) fBall = -1;
        else fBall = int (float (nBall) * Hashff (mod (todCur, 1000.)));
      }
    }
  }
  if (doInit) {
    nStep = 0.;
    fBall = -1;
  }
  if (mId >= 0) {
    if (doInit) Init (mId, rm, vm, qm, wm);
    else Step (mId, rm, vm, qm, wm);
  }
  if (pxId == 4 * nBall + 2) {
    bMid = vec3 (0.);
    bMid2 = 0.;
    if (nStep > 0.) {
      for (int n = VAR_ZERO; n < nBall; n ++) {
        b = Loadv4 (4 * n).xz;
        bMid.xz += b;
        bMid2 += dot (b, b);
      }
      bMid.xz /= float (nBall);
      bMid.y = 0.1;
      bMid2 = sqrt (bMid2 / float (nBall) - dot (bMid.xz, bMid.xz));
    }
  }
  if (pxId < 4 * nBall) {
    kp = 4 * mId;
    if      (pxId == kp + 0) stDat = vec4 (rm, 0.);
    else if (pxId == kp + 1) stDat = vec4 (vm, 0.);
    else if (pxId == kp + 2) stDat = qm;
    else if (pxId == kp + 3) stDat = vec4 (wm, 0.);
  } else {
    kp = 4 * nBall;
    if (pxId == kp) stDat = vec4 (hgSize, trWidf, trHt, nStep);
    else if (pxId == kp + 1) stDat = vec4 (mPtr.xyz, float (fBall));
    else if (pxId == kp + 2) stDat = vec4 (bMid, bMid2);
  }
  Savev4 (pxId, stDat, fragColor, fragCoord);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

vec4 QtMul (vec4 q1, vec4 q2)
{
  return vec4 (
       q1.w * q2.x - q1.z * q2.y + q1.y * q2.z + q1.x * q2.w,
       q1.z * q2.x + q1.w * q2.y - q1.x * q2.z + q1.y * q2.w,
     - q1.y * q2.x + q1.x * q2.y + q1.w * q2.z + q1.z * q2.w,
     - q1.x * q2.x - q1.y * q2.y - q1.z * q2.z + q1.w * q2.w);
}

vec4 RMatToQt (mat3 m)
{
  vec4 q;
  const float tol = 1e-6;
  q.w = 0.5 * sqrt (max (1. + m[0][0] + m[1][1] + m[2][2], 0.));
  if (abs (q.w) > tol) q.xyz =
     vec3 (m[1][2] - m[2][1], m[2][0] - m[0][2], m[0][1] - m[1][0]) / (4. * q.w);
  else {
    q.x = sqrt (max (0.5 * (1. + m[0][0]), 0.));
    if (abs (q.x) > tol) q.yz = vec2 (m[0][1], m[0][2]) / q.x;
    else {
      q.y = sqrt (max (0.5 * (1. + m[1][1]), 0.));
      if (abs (q.y) > tol) q.z = m[1][2] / q.y;
      else q.z = 1.;
    }
  }
  return normalize (q);
}

mat3 LpStepMat (vec3 a)
{
  mat3 m1, m2;
  vec3 t, c, s;
  float b1, b2;
  t = 0.25 * a * a;
  c = (1. - t) / (1. + t);
  s = a / (1. + t);
  m1[0][0] = c.y * c.z;  m2[0][0] = c.y * c.z;
  b1 = s.x * s.y * c.z;  b2 = c.x * s.z;
  m1[0][1] = b1 + b2;  m2[1][0] = b1 - b2;
  b1 = c.x * s.y * c.z;  b2 = s.x * s.z;
  m1[0][2] = - b1 + b2;  m2[2][0] = b1 + b2;
  b1 = c.y * s.z;
  m1[1][0] = - b1;  m2[0][1] = b1;  
  b1 = s.x * s.y * s.z;  b2 = c.x * c.z;
  m1[1][1] = - b1 + b2;  m2[1][1] = b1 + b2; 
  b1 = c.x * s.y * s.z;  b2 = s.x * c.z;
  m1[1][2] = b1 + b2;  m2[2][1] = b1 - b2;
  m1[2][0] = s.y;  m2[0][2] = - s.y;
  b1 = s.x * c.y;
  m1[2][1] = - b1;  m2[1][2] = b1;
  b1 = c.x * c.y;
  m1[2][2] = b1;  m2[2][2] = b1;
  return m1 * m2;
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec3 Hashv3f (float p)
{
  return fract (sin (p + vec3 (37., 39., 41.)) * cHashM);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  float fi;
  fi = float (idVar);
  d = abs (fCoord - vec2 (mod (fi, txRow), floor (fi / txRow)) - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}

