// Variable Triangle Wedge Test - adapted from IQ's distance functions
// Likely already exists elsewhere on shadertoy, but I couldn't find it!

float sdBoundingBox( vec3 p, vec3 b, float e)
{
       p = abs(p  )-b;
  vec3 q = abs(p+e)-e;

  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}


float sdTriangle( in vec3 p, in vec3 q )
{
    p.z += q.z;
    q.z *= 2.0;
    p.x = abs(p.x);
	vec2 a = p.xz - q.xz*clamp( dot(p.xz,q.xz)/dot(q.xz,q.xz), 0.0, 1.0 );
    vec2 b = p.xz - q.xz*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );
    float k = sign( q.z );
    float d = min(dot( a, a ),dot(b, b));
    float s = max( k*(p.x*q.z-p.z*q.x),k*(p.z-q.z)  );
	float sdf= sqrt(d)*sign(s);
    vec2 w = vec2( sdf, abs(p.y) - q.y );
  	return min(max(w.x,w.y),0.0) + length(max(w,0.0));    
}



float map( in vec3 pos )
{
    vec3 size = 0.5+sin(vec3(iTime,iTime*1.5,iTime*2.0))*0.5;
    float xs = 0.5 + (size.x);
    float zs = 0.5 + (size.y);
    float ys = 0.5 + (0.5*size.z);


	float d1 =  sdTriangle(pos,vec3(xs,ys,zs));

    if (iMouse.z>0.5)
        return d1;

    float boff = 0.3;		// bounding box offset
    float bthick = 0.025;	// bounding box thickness
    float d3 =  sdBoundingBox(pos,vec3(xs+boff,ys+boff,zs+boff),bthick);
    d1 = min(d1,d3);
    return d1;
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.001;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) + 
					  e.xxx*map( pos + e.xxx*eps ) );
}

#define AA 2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an = 0.5*iTime;
	vec3 ro = vec3( 5.0*cos(an), 2.5, 5.0*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));



    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.8*ww );

        // raymarch
        const float tmax = 30.0;
        float t = 0.0;
        for( int i=0; i<128; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }

        // shading/lighting
        float v = 1.0-abs(p.y);
        vec3 col = vec3(v*0.1);
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            float dif = clamp( dot(nor,vec3(0.7,0.6,0.4)), 0.0, 1.0 );
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,0.8,0.6));
            col = vec3(0.2,0.3,0.4)*amb + vec3(0.8,0.7,0.5)*dif;
        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}
