#define get(c) texture(iChannel1,vec2(c*2,0.)/iResolution.xy)

#define PI 3.1415926535

// ----------------------------------------------------
// Random functions

#define hash12(c) (fract(sin(vec2(c,sin(c*1000.)*1000.)*1000.)*1000.))
#define rand31(c) (fract(sin(c.x*c.y*c.z/10000.)*1000.))
#define smoothstep(a,b,c) mix(a,b,((sin(clamp(c,0.,1.)*2.-1.)+1.)/2.))
float hash31(vec3 c){
    c = mod(c,1000.);
    vec3 r = floor(c);

    float _00 = smoothstep(rand31(vec3(r.x   ,r.y   ,r.z   )),rand31(vec3(r.x   ,r.y   ,r.z+1.)),fract(c.z));
    float _01 = smoothstep(rand31(vec3(r.x   ,r.y+1.,r.z   )),rand31(vec3(r.x   ,r.y+1.,r.z+1.)),fract(c.z));
    float _10 = smoothstep(rand31(vec3(r.x+1.,r.y   ,r.z   )),rand31(vec3(r.x+1.,r.y   ,r.z+1.)),fract(c.z));
    float _11 = smoothstep(rand31(vec3(r.x+1.,r.y+1.,r.z   )),rand31(vec3(r.x+1.,r.y+1.,r.z+1.)),fract(c.z));

	return smoothstep(smoothstep(_00,_01,fract(c.y)),smoothstep(_10,_11,fract(c.y)),fract(c.x));
}

// ----------------------------------------------------
// Distance estimator

#define fmax(a,b) (a.x>b.x?a:b)
#define fmin(a,b) (a.x<b.x?a:b)
#define cube(p1,p2,size,color) vec4(max(max(abs(p1.x-p2.x),abs(p1.y-p2.y)),abs(p1.z-p2.z))-size/2.,color)
#define sphere(p1,p2,size,color) vec4(length(p1-p2)-size/2.,color)
float capsule(vec3 p1,vec3 p2,vec3 p3,float size){vec3 fr = p3 - p2;return length((p1-p2)-fr*clamp(dot((p1-p2),fr)/dot(fr,fr),0.0,1.0))-size/2.;}
vec4 de(vec3 c){
    vec4 ret0 = fmin(fmin(fmin(vec4(((length(fract(c.xz*5.)-.5)/2.
    +length((texture(iChannel3,c.xz/2.,0.)
    *texture(iChannel3,c.xz/2.*1.4872,0.)).xyz))/15.+c.y+1.+(hash31(c*10000.)/50000.))/1.5,vec3(0.2))
    ,fmin(vec4(c.y+1.024+texture(iChannel2,c.xz,0.).x/100.,vec3(.7)),
    vec4(c.y+1.02+texture(iChannel2,c.xz/10.,0.).x/30.,vec3(.3,.7,.3))
    )),
    sphere(c,vec3(-1,2,0),1.,vec3(6,6,2))
    ),
    vec4(capsule(c,vec3(-1,-1,0),vec3(-1,2,0),0.2)+(hash31(c*10000.)/5000.),vec3(.8))
    );
    //+(hash31(c*100.)/500.);
	return ret0;
}

// ----------------------------------------------------
// March function

float getp(vec4 m,vec3 p){
	return length(m.xyz-p)/500.+0.0001; // raymarching epsilon increase while marching
}

vec4 DoMarch(vec3 Position,vec3 Camera){
	float Iteration = 0.;
    vec3 Marcher = vec3(0);
    float Distance = 10.;

    while(length(Marcher) <= 100. && Iteration <= 500. && Distance >= getp(Marcher.xyzz,vec3(0))){
    	Distance = de(Position+Marcher).x+getp(Marcher.xyzz,vec3(0));
        Marcher += Distance*Camera;
        Iteration++;
    }
    return vec4(Position+Marcher,Iteration);
}

vec3 getnormal(vec3 n){
    vec2 e = vec2(1.0,-1.0)*(n.y<0.002-1.?.00001:.00001);
    return normalize(e.xyy*de(n+e.xyy).x+e.yyx*de(n+e.yyx).x+e.yxy*de(n+e.yxy).x+e.xxx*de(n+e.xxx).x);
}

vec4 getcolor(vec4 m,vec3 p){
    return vec4((length(m.xyz-p)>100.||m.w>=500.?vec3(.2):de(m.xyz).yzw),!(length(de(m.xyz).yzw)>=pow(3.,0.5)));
}

// ----------------------------------------------------
// Main

#define rotate(c,p) (vec3(vec2(cos(p)*c.x+sin(p)*c.z,sin(p)*c.x-cos(p)*c.z),c.y).xzy)

void mn(out vec4 o,vec2 u){
    vec4 Color = vec4(1);
    vec3 Normal;
    float Precision;
    vec2 Looking = get(2).xy+vec2(1.3,0);
    vec3 Camera = rotate(rotate(normalize(vec3((u-iResolution.xy/2.)/iResolution.yy,1.)).yxz,-Looking.y).yxz,Looking.x);
	vec3 Position = -rotate(rotate(normalize(vec3(0,0,1)).yxz,-Looking.y).yxz,Looking.x)*5.;
    if(Position.y < 0.)Position.y = 0.;
    vec4 March = DoMarch(Position,Camera);
    Color *= getcolor(March,Position);

    if(get(2).z == 0.)
    for(int i = 0; i < 100;i++){
        if(length(March.xyz-Position)>100.||March.w >= 500. || Color.w != 1.)break;
        Precision = getp(March,Position)*2.;
        Position = March.xyz;
        Normal = getnormal(Position-Camera*Precision);
    	Camera = reflect(Camera,Normal);
    	March = DoMarch(Position+Normal*Precision,Camera);
        Color *= getcolor(March,Position);
    }
    o = vec4(Color.xyz,1.);

}

void mainImage(out vec4 o,vec2 u){
    vec2 uv = u/iResolution.xy;
    o = vec4(0.);
    vec4 O = vec4(0.);
	int a = 1;
    for(int i = 0; i<a; i++){
        O = vec4(0);
        mn(O,u+hash12(0.3*u.x*u.y*PI*(float(i)+mod(iTime,10.))));
        o += O;
    }
    o /= float(a);

    if(get(2).z == 0. && get(4).z == 0.)
    o = texture(iChannel0,uv)*(1.-(1./get(5)))+o/get(5);
}

#define storable(c,u) (round(u.x/2.) == float(c) && u.y < 1.)
#define get(c) texture(iChannel0,vec2(c*2,0.)/iResolution.xy)
#define store(c,d,u,o) if(storable(c,u))o=d

void mainImage(out vec4 o,vec2 u){
    vec2 r = iResolution.xy;
    vec2 uv = u/r;
    o = texture(iChannel0,uv);

    vec4 Mouse = get(2);
    if(iMouse.z > 0.){
        if(Mouse.z == 0.){
            Mouse.zw = iMouse.xy/100.;
        }else{
            Mouse.xy -= iMouse.xy/100.-Mouse.zw;
            Mouse.zw = iMouse.xy/100.;
        }
    } else {
        Mouse.z = 0.;
    }
    store(2,Mouse,u,o);

    if(get(2).z == 0. && get(4).z == 0.){
        store(5,get(5)+1.,u,o);
    }else{
        store(5,vec4(1.),u,o);
    }

    store(4,vec4(r,r==get(4).xy?0:1,0),u,o);
}
