// alien lab
//
// follow mouse cursor along red lined path to navigate outer walkway and inner chamber
//

const mat2 RQ=mat2(cos(1.57),sin(1.57),-sin(1.57),cos(1.57));
#define PI2 6.2831853

#define X 0.  
//#define X .5  // alternative

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.y, m=iMouse.xy/iResolution.xy-.5;
	vec3 rd = normalize(vec3(uv, .85));
	float v=mod(iTime/6.,PI2), luv=length(uv);
	vec3 p = vec3(0), p1, p2;
	fragColor = vec4(0);
	for (int i = 1; i < 160; i++) {
		p1 = p;
		float k = atan(m.y,m.x);
		p1.xz += vec2(cos(k),sin(k)) * (22.+36.*smoothstep(.18,.22,length(m*iResolution.xy/iResolution.y)));
		p1.yz *= RQ; p2=p1;
		{
			k = length(p1.xy); p1.x=k*(mod(atan(p1.y, p1.x), PI2/6.) -PI2/12.); p1.y=k-38.7;		
			k = length(p1.xz); p1.x=k*(abs(sin(atan(p1.z, p1.x))) -X); p1.z=k-17.0;
		}
		for (int i = 0; i < 2; i++) {
			k = length(p1.xy); p1.x=k*(mod(atan(p1.y, p1.x), PI2/10.) -PI2/20.); p1.y=k-19.2;		
			k = length(p1.xz); p1.x=k*(abs(sin(atan(p1.z, p1.x)))-.50); p1.z=k-17.0; 		
		}
		for (int i = 0; i < 4; i++) {
			k = length(p1.xy); p1.x=k*(mod(atan(p1.y, p1.x), PI2/12.) -PI2/24.); p1.y=k-3.;		
			k = length(p1.xz); p1.x=k*(mod(atan(p1.z, p1.x), PI2 /4.) -PI2/8.);  p1.z=k-3.48;
		}
	        float d = dot(abs(p1), normalize(vec3(5.5,12,14.2))) - 3.1;				
		if (d < .001) {
			fragColor = vec4(10./float(i))*max(.5,-sin(iTime/4.));
   			fragColor.rb*=1.+pow(1./length(p2.xy),2.)*max(0.,sin(40.*sin(iTime)));
			fragColor.b*=1.+1.5-min(1.5,abs(length(p2.xy)-38.4));
			fragColor.gb+=pow(p1.z/3.,10.)*max(0.,sin(iTime/4.));
			if(luv<.3 && luv>.12) if(abs(uv.x)<.006 || luv>.29 || luv<.13) fragColor.r=1.;
			break;
		}
		p += rd * d;
	}
}
