void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //y的偏移值
    float blurOffsetY = 0.25;

    //背景视频的缩放倍数
    float blurTextureScal = 2.0;

    vec4 outPutcolor = vec4(0.0,0.0,0.0,1.0);

     //这里是中间显示不带任何效果的的Texture，根据y方向的偏移进行区域选择
     if (uv.y >= blurOffsetY && uv.y <= 1.0 - blurOffsetY) {
         //原视频
         outPutcolor = texture(iChannel0, uv);
     } else {

         //uv的坐标原点并非在屏幕中心点，所以我们这里需要进行uv坐标的偏移
         vec2 center = vec2(0.5, 0.5);
         //新的uv坐标 uv - center
         uv = uv -  center;
         //纹理放大缩小
         uv = uv / blurTextureScal;
         uv = uv + center;
         outPutcolor = texture(iChannel1, uv);
     }     

     gl_FragColor = outPutcolor;

    fragColor = outPutcolor;
}
