// Aquatic Dynamics
// Leon Denise 2019.08.31
// Licensed under hippie love conspiracy

// Using code from
// Inigo Quilez
// Morgan McGuire

const int count = 6;
const float speed = .4;
const float range = 1.4;
const float radius = .3;
const float blend = .8;
const float balance = .5;
const float falloff = 1.9;
const float grain = .1;

float random(vec2 p) { return fract(1e4 * sin(17.0 * p.x + p.y * 0.1) * (0.1 + abs(sin(p.y * 13.0 + p.x)))); }
mat2 rot(float a) { float c=cos(a),s=sin(a); return mat2(c,-s,s,c); }
float smoothmin (float a, float b, float r) { float h = clamp(.5+.5*(b-a)/r, 0., 1.); return mix(b, a, h)-r*h*(1.-h); }
vec3 look (vec3 eye, vec3 target, vec2 anchor, float fov) {
    vec3 forward = normalize(target-eye);
    vec3 right = normalize(cross(forward, vec3(0,1,0)));
    vec3 up = normalize(cross(right, forward));
    return normalize(forward * fov + right * anchor.x + up * anchor.y);
}

vec3 camera (vec3 eye) {
    vec2 mouse = iMouse.xy/iResolution.xy*2.-1.;
    if (iMouse.z > 0.5) {
        eye.yz *= rot(mouse.y*3.1415);
        eye.xz *= rot(mouse.x*3.1415);
    } else {
        eye.yz *= rot(-3.1415/4.);
        eye.xz *= rot(-3.1415/2.);
    }
    return eye;
}

float geometry (vec3 pos, float time) {
    pos = camera(pos);
    float a = 1.0;
    float scene = 1.;
    float t = time - length(pos) / 4.;
    t = floor(t)+pow(fract(t),.5);
    for (int i = count; i > 0; --i) {
        pos.xy *= rot(cos(t)*balance/a+a*2.+time);
        pos.zy *= rot(sin(t)*balance/a+a*2.+time);
        pos.zx *= rot(sin(t)*balance/a+a*2.+time);
        pos.xz = abs(pos.xz)-range*a;
        scene = smoothmin(scene, length(pos)-radius*a, blend*a);
        a /= falloff;
    }
    return scene;
}

vec3 getNormal (vec3 pos, float time) {
 	vec2 e = vec2(.001,0);
    vec3 p = vec3(0);
 	return normalize(vec3(geometry(pos,time)
                          -vec3(geometry(pos-e.xyy,time),
                                geometry(pos-e.yxy,time),
                                geometry(pos-e.yyx,time))));
}

float raymarch ( vec3 eye, vec3 ray, float time, out float total ) {
    float dither = random(ray.xy+fract(iTime));
    total = 0.0;
    const int count = 20;
    for (int index = count; index > 0; --index) {
        float dist = geometry(eye+ray*total,time);
        dist *= 0.9+.1*dither;
        total += dist;
        if (dist < 0.001 * total)
            return float(index)/float(count);
    }
    return 0.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.*(fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 eye = (vec3(0,0,4.));
    vec3 ray = look(eye, vec3(0), uv, 1.5);
    float total = 0.;
    float dither = random(ray.xy+fract(iTime));
    float time = iTime*speed + dither*grain;
    float shade = raymarch(eye, ray, time, total);
    vec3 pos = eye + ray*total;
    vec3 normal = getNormal(pos, time);
    vec3 rf = reflect(ray,normal);
    vec3 color = vec3(.2);
    color += vec3(.4,.5,.99)*pow(dot(normal,vec3(0,1,0))*.5+.5,.5);
    color += vec3(.7,.8,.7)*pow(dot(normal,vec3(0,-1,0))*.5+.5,2.);
    color += vec3(.9,.0,.0)*smoothstep(4.,0.,total);
    color += .2*vec3(.1,.9,.2)*smoothstep(3.,5.,total);
    fragColor = vec4(color*shade,1);

    vec4 frame = texture(iChannel0, fragCoord/iResolution.xy);
    float fade = (1.-iTimeDelta*2.);
    fragColor = max(fragColor, frame*fade-dither*iTimeDelta);
}
