#define PI 3.141592
#define time iTime
mat2 r(float a){ return mat2(cos(a),sin(a),-sin(a),cos(a)); }
vec2 q(float a){ return -vec2(cos(a),sin(a))/(9.+sin(-time)*3.);}
float o(vec2 uv) {
    return  smoothstep(0.1,0.2,smoothstep(0.12,0.17,length((uv))) - smoothstep(0.1,0.3,length(uv)))  ;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =  (fragCoord-.5*iResolution.xy)/iResolution.y;
vec2 uvv = uv;
    float mo = 1.5;
    uv*=10.;

    uv = mod((uv+vec2(cos(-time)+5.,sin(time)+2.))*r(time/10.),mo+abs(sin(length(uvv)-time)));

    uv-=(mo/4.)+abs(sin(length(uvv)-time));
     uv*=r(1.*smoothstep(-.01,0.02,(length(uvv)-tan(-time)))*time+sin(abs(length(uvv))*5.));
    uv *= 1.-length(uv/sqrt(abs(uv)));
    uv*=2.;

    float d = smoothstep(
    1.0,
    2.5,
      o(uv+q(time)) + o(uv+q(-time+PI/2.))+ o(uv+q(time+PI)) + o(uv+q(-time+(3.*PI/2.) ))
    ) ;

    vec3 col = mix(vec3(.1*d,.1,.1),vec3(0.5*d+(iChannel0,fract(cos(time)+uv*10.)).x/2.,0.6+(iChannel0,fract(sin(time)+uv*10.)).y/2.,.9+d/10.),vec3(d));
    fragColor = vec4(
        col,
        1.0);
}
