float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 kink(vec2 p, vec2 c, float k) {
    p -= c;
    //to polar coordinates
    float ang = atan(p.x, p.y);
    float len = length(p);
    //warp angle with sigmoid function
    ang -= ang/sqrt(1.+ang*ang)*(1.-k);
    //to cartesian coordiantes
    return vec2(sin(ang),cos(ang))*len + c;
}

float sdTri( in vec2 p, in vec2 q )
{
    p.x = abs(p.x);
    vec2 a = p - q*clamp( dot(p,q)/dot(q,q), 0.0, 1.0 );
    vec2 b = p - q*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );
    float s = -sign( q.y );
    vec2 d = min( vec2( dot(a,a), s*(p.x*q.y-p.y*q.x) ),
                  vec2( dot(b,b), s*(p.y-q.y)  ));
    return -sqrt(d.x)*sign(d.y);
}
float sdBox( in vec2 p, in vec2 q )
{
    p = abs(p) - q;
    return max(p.y,p.x);
}


float sdLine(vec2 p, vec2 A, vec2 B, float bally){
    vec2 AB = B - A;

    float angle = atan(AB.y, AB.x);

    vec2 n = normalize(vec2(sin(-angle),cos(-angle)));

    float d = dot(p - B,n);

    d = abs(d);
    if(bool(bally)){
        if(dot(p - A,AB) < 0.)
            d = length(p - A);
