    } else if(mode == 9){
    	// 9 linear light
    	co = mix(ca, length(cb) > .5 ? ca + 2.0 * (cb - .5) : ca + 2.0 * cb - 1.0, odeb);
    } else if(mode == 10){
    	// 10 screen
    	co = mix(ca, 1.0 - (1.0 - ca) * (1.0 - cb), odeb);
    } 

    return co;
