float seed = 0.;
const int cnt = 5;



float bspline( in vec2 coords )
{
    float id = floor(mod(frame/period,2.));
    seg = floor(frame/period/2. );

    vec2 roll = r12(seg);

    coords *= rot(-pi*roll.x*0.1);

    vec2 oc = coords;

    float t = coords.x*float(cnt)*1. + 1.0;
    float[cnt] cp;

    float res = 0.;

    float amp = 0.03;

    vec4 na = valueNoise(vec2(t*4. + seed,0),1.3);
    vec4 nb = valueNoise(vec2(t*19. + seed,0),1.3);

    amp += (na.x*0.7 - nb.x*0.01)*pow(roll.y,4.);

    t += na.x*0.5;

    for(float i = 0.; i < float(cnt); i++){
    	int idx = int(i);

        cp[idx] = r11(i + seed)*amp;


        res += cp[idx] * N_i_3(t, i);
    }

    //res -= smoothstep(0.,1.,t*0.12)*0.5;

    res -= max(smoothstep(0.,1.,guv.x*0.4 + 0.1),0.);

    res += max(smoothstep(0.,1.,-guv.x*0.27),0.);

    //res -= clamp(0.,1.,guv.x);
    //res -= smoothstep(0.,1.,guv.x*1.42)*0.5;

    //res += sin(t + cos(t*2.)*0.01)*0.07;

    //res += sin(t + cos(t*2.)*0.01)*0.07;


    float v = res - coords.y;

    float slope = dFdx(v) / dFdx(coords.x);
    float d = abs(v)/length(vec2(slope, -1.0)); 

    d = max(d,-coords.x - 0.2);
    d = max(d,coords.x - 0.7);

    return d;
}

