const float R = 5.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 offset = vec2(1.0) / iResolution.xy;

    vec3 final_c = surfaceBlur(iChannel0, uv, R, offset);

    if(fragCoord.x > mod(iTime * 150.0, iResolution.x))
        final_c = texture(iChannel0, uv).rgb;

    fragColor = vec4(final_c, 1.0);
}

const float Y = 78.0;

vec3 surfaceBlur(in sampler2D tex, in vec2 uv, float r, in vec2 offset){

    // rgb Separate calculation
    vec3 center_c = texture(tex, uv).rgb;
    vec3 sum_c = vec3(0.0);
    for(int i = 0; i < 3; i++){
    	float sum_i = 0.0;
        float norm = 0.0;
        for(float x = -r; x <= r; x++){

           	for(float y = -r; y <= r; y++){
               	vec2 uv2 = uv + vec2(x * offset.x, y * offset.y);
               	vec3 cur_c = texture(tex, uv2).rgb;

                float para = 1.0 - abs(center_c[i] - cur_c[i]) * 255.0 / (2.5 * Y);
				sum_i += para * cur_c[i] * 255.0;
                norm += para;
           	}
        }
        sum_c[i] = sum_i / norm;
    }

	return sum_c / 255.0;
}
