vec2 e (float x, float t, float a) {
	return step(0.,t)*exp(-6.2/3.*t*a)*vec2(clamp(1.1*sin(6.2*x*t+sin(.01*6.2*x*t)),-1.,1.));
}
vec2 mainSound( in int samp, float time )
{

    vec2 w = vec2(0);
    float m = 1.;
    float dm = 0.;
    float p =  1.;
    float n =  .5;
    float dp = 0.;
    float dn = 0.;
    float r = 1.;
    float g = -.5;
    float b = .5;
    float dr = 0.;
    float dg = 0.;
    float db = 0.;
    float o = 1.;
    for (float i = 0.; i < 15.; i++) {

    	w += e (300.+m*25., time - o*i,1.);
    	w += .5*e (500.+n*50., time - o*i,2.);
    	w += .5*e (500.+p*50., time - o*i-o/2.,2.);
    	w += .3*e (400.+r*50., time - o*i-o*1./4.,3.);
    	w += .3*e (400.+g*50., time - o*i-o*2./4.,3.);
    	w += .3*e (400.+b*50., time - o*i-o*3./4.,3.);

        for (int j = 0; j < 4; j++) {
            float mag = sqrt(m*m+n*n+p*p+r*r+g*g+b*b);
            float np = sqrt(n*n+p*p);
            float rgb = sqrt(r*r+g*g+b*b);

            dm += m/mag - sign(m);
            dn += n/mag - n/np ;
            dp += p/mag - p/np ;
            dr += r/mag - r/rgb ;
            dg += g/mag - g/rgb ;
            db += b/mag - b/rgb ;

            m += dm;
            n += dn;
            p += dp;
            r += dr;
            g += dg;
            b += db;
        }
    }

    return w*.5;
}
