void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 base = texture(iChannel0,uv);

    vec4 c_color = texture(iChannel0,uv);
    int width = 1;
    float diff = 0.0;
    for (int i = -width; i <= width; i++) {
        for (int j = -width; j <= width; j++) {
            vec4 color = texture(iChannel0,uv + vec2(i, j)/iResolution.xy);
            if (color != c_color) {
                diff += 0.1;
            }
        } 
    }  	
    fragColor = vec4(base.rgb - vec3(diff),0.0);    
}

// Noise from https://www.shadertoy.com/view/4sdGD8
lowp vec3 permute(in lowp vec3 x) { return mod( x*x*34.+x, 289.); }
lowp float snoise(in lowp vec2 v) {
  lowp vec2 i = floor((v.x+v.y)*.36602540378443 + v),
      x0 = (i.x+i.y)*.211324865405187 + v - i;
  lowp float s = step(x0.x,x0.y);
  lowp vec2 j = vec2(1.0-s,s),
      x1 = x0 - j + .211324865405187, 
      x3 = x0 - .577350269189626; 
  i = mod(i,289.);
  lowp vec3 p = permute( permute( i.y + vec3(0, j.y, 1 ))+ i.x + vec3(0, j.x, 1 )   ),
       m = max( .5 - vec3(dot(x0,x0), dot(x1,x1), dot(x3,x3)), 0.),
       x = fract(p * .024390243902439) * 2. - 1.,
       h = abs(x) - .5,
      a0 = x - floor(x + .5);
  return .5 + 65. * dot( pow(m,vec3(4.))*(- 0.85373472095314*( a0*a0 + h*h )+1.79284291400159 ), a0 * vec3(x0.x,x1.x,x3.x) + h * vec3(x0.y,x1.y,x3.y));
}

////////////////////////
// fields for figures //
////////////////////////

#define N 79.0
#define M 53.0

const float log_step = N/M;
const float extent   = 7.0;

vec2 uv(vec2 p) {
    vec2 f = vec2(p) + 2.0 * snoise(p.xy*0.1 + 0.05*iTime);
    return f.xy;
}

float d(vec2 p) {

    vec2 ctr = vec2(extent/2.0*iResolution.x/iResolution.y + extent/2.0*sin(iTime), extent/2.0 + cos(iTime*0.7));
    vec2 dt  = vec2(ctr-p) / extent;

    return log_step * 2.0; // uncomment to reveal distortion

    float delta = 1.0;
    float dudx = 1.0 * ( uv(p - vec2(delta,0.0)).x - uv(p + vec2(delta,0.0)).x ) * 0.5 / delta;
    float dudy = 1.0 * ( uv(p - vec2(0.0,delta)).x - uv(p + vec2(0.0,delta)).x ) * 0.5 / delta;
    float d = log_step / (0.000001 + length(vec2(dudx,dudy)));

    d *= (1.0 + 1.0 / (0.05 + dot(dt,dt)));

    return max(log_step,d);
}

