vec4 draw(vec2 p) {
    float d = d(p);
    float u = uv(p).x;
    float ctr = 0.0;
    int lid = getLocalID(u, d, ctr);
    uvec4 gid = getGlobalID(lid, d);

    if (gid == uvec4(0u)) return vec4(1.0);        

    return getRandomColor(gid);

}

/**********************************************/
/**********************************************/
/**********************************************/

float quantize(float d) {
    return pow(log_step,floor(log(d)/log(log_step)));
}

int d2level(float d) {
    return int(floor(log(d)/log(log_step)));
}

float level2d(int level) {
  return pow(log_step,round(float(level)));
}

float interpolate(float d) {
    int   level   = d2level(d);
    float df      = level2d(level);
    float dc      = level2d(level-1);    
    float linear  = (d - df) / (df - dc) / log_step;
    return smoothstep(0.0,1.0,linear);
}

float spacing(float d) {
    return 1.0/d;   
}

float shift(float d) {
    return d ;
}

float id2Lborder(int id, float d) {
    return (float(id) +       (shift(d) / spacing(d)) ) * spacing(d);
}

float id2center(int id, float d) {
    return (float(id) + 0.5 + (shift(d) / spacing(d)) ) * spacing(d);
}

int u2id(float u, float d) {
    return int(floor( u / spacing(d) - (shift(d) / spacing(d)) ));
}

int u2closestLBorderId(float u, float d) {
    int lid = u2id(u, d);
    if (u < id2center(lid, d)) {
        return lid;
    } else {
        return lid+1;
    }
}

float u2closestBorder (float u, float d) {
    return id2Lborder(u2closestLBorderId(u,d), d);
}

/**********************************************/

int getLocalID(float u, float d, out float ctr) {
  // fine and coarse densities
  int   level = d2level(d);
  float df    = level2d(level  );
  float dc    = level2d(level-1);
  // enclosing band id
  int   lid     = u2id(u, df); 
  // get the borders of the fine band
  float lf_brdr = id2Lborder(lid, df);
  float rf_brdr = id2Lborder(lid+1, df);
  // get the closest coarser band borders
  float lc_brdr = u2closestBorder(lf_brdr, dc);
  float rc_brdr = u2closestBorder(rf_brdr, dc);
  // interpolate between fine and coarse borders
  float a     = interpolate(d);
  float left  = lf_brdr * a + lc_brdr * (1.0 - a);
  float right = rf_brdr * a + rc_brdr * (1.0 - a);    
  // adjust enclosing band after interpolation
  if      (u < left ) lid--;
  else if (u > right) lid++;

  if (abs(left - right) < 0.5 * spacing(df)) {
    ctr = -99999.0;
  } else {
    ctr = (left + right)/2.0;
  }

  return lid;
}

/**********************************************/

uvec4 setId(uvec4 id,uint bit) // 128 bits IDs
{
    if (bit > 127u) return id;
    if (bit < 64u) {
        if (bit < 32u) { // 0 - 31 (w) LSBs
            return uvec4(id.x,id.y,id.z,id.w | (1u << bit));
        } else {         // 32 - 63 (x)
            return uvec4(id.x,id.y,id.z | (1u << (bit-32u)),id.w);            
        }
    } else {
        if (bit < 96u) { // 64 - 95 (x)
            return uvec4(id.x,id.y | (1u << (bit-64u)),id.z,id.w);            
        } else {         // 96 - 127 (x) MSBs
            return uvec4(id.x | (1u << (bit-96u)),id.y,id.z,id.w);            
        }
    }
}

/**********************************************/

uvec4 getGlobalID(int lid, float d) {
  uvec4 gid = uvec4(0u);
  int start_level = d2level(d);
  for (int level = start_level; level > 0; level--) {
    float df      = level2d(level);
    float dc      = level2d(level-1);
    float lf_brdr = id2Lborder(lid  , df);
    float rf_brdr = id2Lborder(lid+1, df);
    // get the closest borders on the coarse bands
    int lid_brdr = u2closestLBorderId(lf_brdr, dc);
    int rid_brdr = u2closestLBorderId(rf_brdr, dc);
    if (lid_brdr == rid_brdr) {
      // this band is closing between parents, tag in ID (lower 96 bits)
      gid = setId(gid,uint(96 - level));
      lid  = u2id(lf_brdr, dc);
      if (level == start_level) {
        // the start band is appearing
        //gid.x = uint(lid_brdr);
        //return uvec4(0);
      }
    } else {
      // move up to parent
      lid  = lid_brdr;

    }      
  }
  // parent ids go into the most significant 32 bits
  gid.x = uint(lid);
  return gid;
}

