void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    float balanco=(sin((2.0*iTime)+(uv.x*1.0))/6.0);


    // Output to screen

    if(uv.y>0.9+balanco)
        fragColor = vec4(0,0,0,0)*1.0;


    else if(uv.y>0.6+balanco)
        fragColor = vec4(1,1,0,0)*1.0;




                else if(uv.y>0.3+balanco)
        fragColor = vec4(0,1,0,1)*1.0;





                else if(uv.y>0.01+balanco)
        fragColor = vec4(1,0,0,0)*1.0;







                    }
