// Fork of "Stroboscopic motion image" by Yambam. https://shadertoy.com/view/wsjfzW
// 2020-05-16 17:00:31

void mainImage( out vec4 O, in vec2 U )
{
    U /= iResolution.xy;
    O = texture(iChannel0, U);
}

#define BLURINESS 1.     //Nice values between 0.8 and 0.99
#define CHROMA_THRESH .4  //Nice values between .25 and .65

#define ZOOM0 1.
#define DITHER0 .012

#define ZOOM1 1.
#define DITHER1 .006 //.01 //

#define MOTION_BLUR 8.    //Nice values between 0. and 10.

#define PASS_COUNT 20.

void mainImage( out vec4 final_O, in vec2 _U )
{
    vec2 U = _U / iResolution.xy;
    vec2 origin = vec2(.5,.35);

    final_O = vec4(0.,0.,0.,1.);
    for (float j=0.; j<PASS_COUNT; j++) {
        vec4 O = vec4(0.,0.,0.,1.);

        float shift = (_U.x)/1.22074408461 +
                      (_U.y)/1.4902161201 +
                      ((float(iFrame)*PASS_COUNT+j)/1.61803398875)*pow(iResolution.x,.5)/60./1.8191725134; // * iMouse.x/iResolution.x; //

        float shift_y = mod(shift/1.32471795724,1.);
        float shift_x = mod(shift/1.75487766623,1.);

		bool off = shift_x*shift_x + shift_y*shift_y > 1./DITHER1/MOTION_BLUR*DITHER1;
        //bool off = shift_x*shift_x + shift_y*shift_y > 1.;


        vec4 tex0, tex1;

        //if (!off) {
            vec2 Ut0 = (U-origin)/ZOOM0+origin+DITHER0*vec2(-1.+2.*(mod(shift_x,1.)),
                                                            -1.+2.*(mod(shift_y,1.)));
            tex0 = texture(iChannel0, Ut0);
        //}
        if (j>=1.
        &&  iMouse.z>0.) {
            //Fun sharpness adjustment!
        	tex0 = mix(tex0, mix(vec4(.0,.0,.0,1.), final_O, 1./j), -1.5+4.*iMouse.x/iResolution.x);
        }


        if (!off) {
            vec2 Ut1 = (U-origin)/ZOOM1+origin-DITHER1*vec2(-1.+2.*(mod(shift_x,1.)),
                                                            -1.+2.*(mod(shift_y,1.)));
            tex1 = texture(iChannel1, Ut1); //U); //
        }


        float chroma = 1.; //(max(distance(vec3(tex1.r,tex1.g,tex1.b), vec3(13./255., 163./255., 37./255.)), CHROMA_THRESH)-CHROMA_THRESH)/(1.-CHROMA_THRESH);

        //O = tex0;
        vec4 bg_col = vec4(0.085 + 0.085*cos(iTime+U.xyx+vec3(0,2,4)), 1.); //vec4(.25,.0,.4,0.) //
        O = mix(tex0, bg_col, 1.-BLURINESS); //mix(O, bg_col, 1.-BLURINESS);
        if (!off) {
        	O = mix(O, tex1, (0.+1.*chroma));
        }
        //O = O - vec4(.01,.01,.01,.0);

    	final_O += O;
    }

    final_O = mix(vec4(.0,.0,.0,1.), final_O, 1./PASS_COUNT);
}
