float Circle(vec2 uv, vec2 p, float r, float blur){

    float d = length(uv-p);

    return smoothstep(r,r-blur,d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;

    vec2 uvo = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    uvo.x+=.3;

    vec2 gvo = fract(uvo*5.)-.5;

    vec2 st = vec2(atan(uvo.x,uvo.y), length(uvo));

    vec2 uv = vec2(st.x/6.2831-.5,st.y*20.);


    float cr =sin(t*1.)*.1 + .15;

    float b = mod(t,1.);
    float c = 0.;
	float mask = 0.;
    vec2 gv = fract(uv*6.)-.5;

    vec3 col = vec3(0);
    for( float x = -1.;x<=1.;x++){
        for(float y=-1.;y<=1.;y++){
			float c2 =Circle(vec2(gv.x-x,gv.y-y), 
                             vec2((b-.5)+(uv.y-y)/50.,
                                  sin(t*.1+uvo.y*10.)),
                             .75,
                             .9);


            float c3 =Circle(vec2(gvo.x-x,gvo.y-y), 
                             vec2((b-.5),
                                  uv.x+(fract(uv.x*5.)-(fract(uv.x)))
                                  +.2)+sin(t*.5)*uvo.x/5.,
                             .2*sin(uvo.y*4.-4.7)+uvo.x/4.+.2,
                             .4*uvo.x+.25);


	   	 	c+= c2;
            mask+=c3/2.;

            col= vec3(sin(t)/(-uv.y/10.),0.25,cos(t)+uv.y/15.);
        }
    }

    fragColor = vec4(mask*c*col,1.0);
    fragColor*=10.;
}
