vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{//from https://iquilezles.org/articles/palettes
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec2 tv = uv;
    uv = -1. + 2. * uv;
    //vec2 tv = inData.v_texcoord;
    vec3 col = vec3(0.);
    float time = iTime;
    //float stime = sin(time);
    //float ptime = -4.2+time;
    time += 90.;
    time = abs(mod(time*1.,180.)-90.);
    //uv.x += time*0.1;

    //uv = tv;
    //uv = mix(uv,tv,0.99);

    //uv.y += sin(uv.x*2.);
    //uv.y += abs(fract(uv.x*4.)-0.5);
    float us = 0.1*sin(time*0.04);
    float tc = clamp(iTime,60.5,iTime+1.)-60.5;
    float tc1 = clamp(iTime,24.,iTime+1.)-24.;
    uv.x *= tc1*0.05;
    //uv *= 1.+(100./(iTime+10.));
    //uv.y += time;
    uv.y -= time*0.05;
    for (float i=0.;i<12.;i++) {
        uv.y += sin(uv.x*i)*us;
        uv.x += time*0.002*sin(time*0.01);
        uv.y += abs(fract(uv.x*4.*i)-0.5)*us;
    }
    //uv.y += time*10.;

    //uv.y *= sin(time*10.)*.5+0.5;
    uv = mix((tv*sin((iTime+2.2)*3.14159*(0.5*(1./(time+1.))))),uv,0.1);
    //uv *= 0.1;
    //time = abs(mod(time,74.)-74.);
    uv *= vec2(sin((uv.y+uv.x)*1.));
    col += uv.y;
    col.r += time*0.01;
    time -= 4.2;
    //time *= 0.9;
    //col = abs(col);
    //col.r = col.r*col.r*-1.;
    col = palette(col.r*((time-tc*0.5)),vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,0.7,0.4),vec3(0.,0.15,.2));
    col *= vec3(0.6,0.6,1.);
    //col.rg = fract(uv*2.);
    fragColor = vec4(col,1.);
}
