#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define i vec2(0,1)
vec2 mul (vec2 a, vec2 b) {
	return vec2(a.x*b.x-b.y*a.y,a.x*b.y+a.y*b.x);
}
void mainImage( out vec4 Q, in vec2 U )
{
   Q = A(U);
   vec4
       n = A(U+vec2(0,1)),
       e = A(U+vec2(1,0)),
       s = A(U-vec2(0,1)),
       w = A(U-vec2(1,0)),
       m = 0.25*(n+e+s+w),
       l = m-Q;
    float c = .5, b = .01;

    Q.x += c*(l.y +b*(Q.y-dot(Q.xy,Q.xy)*Q.y));
    Q.y += c*(Q.x);


    //Q.x += c*(l.y -b*cos(Q.y));
    //Q.y += c*(Q.x);


    if (iFrame < 1) Q = .1*sin(.01*length(U-0.5*R)*vec4(1,2,3,4));
    if (iMouse.z>0.&&length(U-iMouse.xy)<30.) Q = .1*sin(iTime*vec4(1,2,3,4));
}
