// Kazimir Malevich, Black Square, 1915

#define MARGIN .08

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    vec2 p = (fragCoord - .5*R) / R.y;

    vec2 q = step(.5-MARGIN, abs(p));
    float col = q.x + q.y;

    fragColor = vec4(vec3(col),1.0);
}
