vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mc = iMouse.xy/iResolution.xy;
    float theta = 1.123;
    float r = (1.0 - cos(theta))/2.0;
    float pre =  -1.78999841099374081749;//r*cos(theta)+0.25;
    float pim =  0.0;//r*sin(theta);

    float k = pow(1.01,float(iFrame));
    float xmin = pre-2.4/k;
	float xmax = pre+1.4/k;
	float ymin = pim-1.0/k;
	float ymax = pim+1.0/k;

    float q = pow(1.004,float(iFrame));
	float limit = 30.0 * q;
    float zre = 0.0;
    float zim = 0.0;
    float cre = (uv.x * (xmax-xmin))+xmin;
    float cim = (uv.y * (ymax-ymin))+ymin;
    float count = 0.0;


    for(float i = 0.0;i<limit;i++){
        float zsqre = zre*zre - zim*zim;
        float zsqim = 2.0*zre*zim;
        zre = zsqre + cre;
        zim = zsqim + cim;
        count = count + 1.0;
        if(zre*zre + zim*zim > 4.0){
        	break;
        }
    }

   	vec3 hsbcol = vec3(count*0.01,1.0,1.0);
	vec3 col = hsv2rgb(hsbcol);

    if (count+0.1 > limit) {
      col = vec3(0.0,0.0,0.0);   
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}
