void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float vfun = pow(abs(1.0-(uv.x+uv.y)+sin(iTime*2.0)*0.8),0.1);
    uv += vec2(iTime*0.1,0.);

    vec3 col = mix( vec3(4.0,.0,.0), vec3(0.,0.,0.),
    vfun );
    col = mix( vec3(0.5), col, vfun ); 

    // Output to screen
    float crust = smoothstep( vfun*0.8,vfun, texture(iChannel1,uv*2.0).g );
    col = vec3( crust+col );
    fragColor = vec4(col,1.0);
    fragColor.xyz -= textureLod(iChannel0,fragCoord/8.0,0.0).r/7.;
}

vec2 mainSound( int samp, float time )
{
    float getmad = ( cos(time*4.0) * 0.5 )+0.7;
    getmad *= 0.4;
    float flanger = time*2.0;
    flanger = cos(flanger)*0.0005;
    flanger = time+flanger;

    float square = sign( fract(95.9*time)-0.5 );
    float saw = fract(square*96.*time);
    float saw2 = fract(square*96.*flanger);
    float mixedsaw = mix( saw, saw2, .5 );
    return vec2( mixedsaw )*getmad;
}
