}

void mainImage( out vec4 U, in vec2 pos )
{
    ivec2 p = ivec2(pos);

    //deformation gradient
   	mat2 D = mat2(0);
    float M = 0.;

    //basically integral over all updated neighbor distributions
    //that fall inside of this pixel
    //this makes the tracking conservative
    range(i, -1, 1) range(j, -1, 1)
    {
        vec2 tpos = pos + vec2(i,j);
        vec4 data = T(tpos);

        vec2 X0 = DECODE(data.x) + tpos;
    	vec2 V0 = DECODE(data.y);
       	mat2 D0 = mat2(T1(tpos));

        X0 += V0*dt; //integrate position

        //particle distribution size
        float K = distribution_size;

        vec4 aabbX = vec4(max(pos - 0.5, X0 - K*0.5), min(pos + 0.5, X0 + K*0.5)); //overlap aabb
        vec2 center = 0.5*(aabbX.xy + aabbX.zw); //center of mass
        vec2 size = max(aabbX.zw - aabbX.xy, 0.); //only positive

        //the deposited mass into this cell
        vec3 m = data.z*vec3(center, 1.0)*size.x*size.y/(K*K);

        //add deformation grad weighted by mass
        D += D0*m.z;

        //add mass
        M += m.z;
    }

    //normalization
    if(M != 0.)
    {
       D /= M;
    }
	else D = mat2(1.0);

    //initial condition
    if(iFrame < 1)
    {
        D = mat2(1.0);
    }

    U = vec4(D);
}

//velocity update


float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float border(vec2 p)
{
    float bound = -sdBox(p - R*0.5, R*vec2(0.49, 0.49)); 
    float box = sdBox((p - R*vec2(0.5, 0.6)) , R*vec2(0.05, 0.01));
    float drain = -sdBox(p - R*vec2(0.5, 0.7), R*vec2(0.0, 0.0));
    return bound;
}

#define h 1.
vec3 bN(vec2 p)
{
    vec3 dx = vec3(-h,0,h);
    vec4 idx = vec4(-1./h, 0., 1./h, 0.25);
    vec3 r = idx.zyw*border(p + dx.zy)
           + idx.xyw*border(p + dx.xy)
           + idx.yzw*border(p + dx.yz)
           + idx.yxw*border(p + dx.yx);
    return vec3(normalize(r.xy), r.z + 1e-4);
}

vec2 normalize2(vec2 a)
{
    return all(equal(a, vec2(0.)))?vec2(0.):normalize(a);
}


mat2 strain(mat2 D, float M0)
{

    float J = abs(determinant(D)) + 0.01;

    // MPM course, page 46
    float volume = J*M0;

    // useful matrices for Neo-Hookean model
    mat2 F_T = transpose(D);
    mat2 F_inv_T = inverse(F_T);
    mat2 F_minus_F_inv_T = D - F_inv_T;

    // MPM course equation 48
    mat2 P_term_0 = elastic_mu * (F_minus_F_inv_T);
    mat2 P_term_1 = elastic_lambda * log(J) * F_inv_T;
    mat2 P = P_term_0 + P_term_1;

    // cauchy_stress = (1 / det(F)) * P * F_T

    // equation 38, MPM course
    mat2 stress = (1./J)*P * F_T;

    // (M_p)^-1 = 4, see APIC paper and MPM course page 42
    // this term is used in MLS-MPM paper eq. 16. with quadratic weights, Mp = (1/4) * (delta_x)^2.
    // in this simulation, delta_x = 1, because i scale the rendering of the domain rather than the domain itself.
    // we multiply by dt as part of the process of fusing the momentum and force update for MLS-MPM
    return volume * stress;
}

void mainImage( out vec4 U, in vec2 pos )
{
    vec2 uv = pos/R;
    ivec2 p = ivec2(pos);

    vec4 data = T(pos); 
    vec2 X = DECODE(data.x) + pos;
    vec2 V = DECODE(data.y);
    float M = data.z;
    float C = data.w;
    if(M != 0.) //not vacuum
    {
        //Compute the force
        vec2 Fa = vec2(0.);
         vec2 F = vec2(0.);
        float a = 0.;
        float tM = 0.;
        vec2 avgV = vec2(0.);
        range(i, -2,2) range(j, -2, 2)
        {
            if(!(i == 0 && j == 0))
            {
                vec2 tpos = pos + vec2(i,j);
                vec4 data = T(tpos);

                vec2 X0 = DECODE(data.x) + tpos;
                vec2 V0 = DECODE(data.y);
                float M0 = data.z;
                vec2 dx = X0 - X;
                vec2 dv = V0 - V;
                mat2 D0 = mat2(T1(tpos));
                float weight = GS(0.8*dx);
                avgV += M0*V0;
				tM += weight*M0;
                Fa += M0*M*MF(dx,dv)*normalize2(dx);
                F += clamp(strain(D0, M0)*dx, vec2(-1.0), vec2(1.0))*weight;
                a += M0;
            }
        }
        avgV /= a;

        F += (V - avgV)*0.1;

        if(iMouse.z > 0.)
        {
            vec2 dx= pos - iMouse.xy;
            F -= 0.0002*dx*GS(dx/80.);
        }

       	//gravity
        F += 0.001*vec2(0,-1);

        //integrate velocity
        V += (F + Fa/M)*dt;


        vec3 BORD = bN(X);
        V += 0.6*smoothstep(0., 5., -BORD.z)*BORD.xy;
        //velocity limit
        float v = length(V);
        V /= (v > 1.)?1.*v:1.;
    }

    //save
    X = X - pos;
    U = vec4(ENCODE(X), ENCODE(V), M, C);
}

