{
    return all(equal(a, vec2(0.)))?vec2(0.):normalize(a);
}

void mainImage( out vec4 U, in vec2 pos )
{
    vec2 uv = pos/R;
    ivec2 p = ivec2(pos);

    vec4 data = T(pos); 
    vec2 X = DECODE(data.x) + pos;
    vec2 V = DECODE(data.y);
    float M = data.z;
    float C = data.w;
    mat2 D = mat2(T1(pos));
    if(M != 0.) //not vacuum
    {
        //Compute the velocity matrix
        mat2 B = mat2(0.);
        float a = 0.5;
        range(i, -2, 2) range(j, -2, 2)
        {
            if(!(i == 0 && j == 0))
            {
                vec2 tpos = pos + vec2(i,j);
                vec4 data = T(tpos);

                vec2 X0 = DECODE(data.x) + tpos;
                vec2 V0 = DECODE(data.y);
                float M0 = data.z;
                vec2 dx = X0 - X;
                vec2 dv = V0 - V;
                float weight = GS(0.65*dx);
                B += mat2(dv*dx.x,dv*dx.y)*weight;
                a += weight;
            }
        }
        B /= a;

        //density correction
        B -= 0.03*mat2(M - 1.0);

        //integrate deformation gradient
       	D += dt*B*D;

        //smoothing
        D = D*(1. - relax) + mat2(1.)*relax;
    }

    //save
    U = vec4(D);
}
