// ^^^ Switch Between Buffer A and B ^^^
// This is for Rendering and Settings

// ##### COMMON VALUES #####

// Change these Parameters to Your Liking!
// Maximum Number of Marches,
// You want it to limit the raymarcher before the max distance parameter or it will look bad.
#define MAX_MARCHES 64

// Redundant for this idk if the max marches are large and you see ugly stuff then increase this
#define MAX_DISTANCE 6.5

// fBm Number of Octaves (Detail)
#define NUM_OCTAVES 8

// Size of Steps, smaller means more sampling over depth but also means more computation.
// Increase max marches if the scene goes invisible.
#define STEP_SIZE 0.1

// If you march less rays, the nebula will appear darker. Bump this up to make it brighter again.
#define DENSITY 1.0

// Tiles, more means easier on the Hardware
#if HW_PERFORMANCE==0
#define TILES 15
#else
#define TILES 2 // Set to 1 if you have a computer that can handle it so it will render faster
#endif          // (most shouldn't time out, except for like Intel HD Graphics)
// So kindly made by LoicVDB uwu

// Amount of distortion to apply
#define DISTORT_AMOUNT 2.5

// Oof ugly mess below watch out lol

bool isKeyPressed(int KEY){
	return texelFetch( iChannel3, ivec2(KEY,0), 0 ).x > 0.5;
}

// ##### NOISE #####
float mod289(float x){return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec4 mod289(vec4 x){return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec4 perm(vec4 x){return mod289(((x * 34.0) + 1.0) * x);}
float noise(vec3 p){
    vec3 a = floor(p);
    vec3 d = p - a;
    d = d * d * (3.0 - 2.0 * d);
    vec4 b = a.xxyy + vec4(0.0, 1.0, 0.0, 1.0);
    vec4 k1 = perm(b.xyxy);
    vec4 k2 = perm(k1.xyxy + b.zzww);
    vec4 c = k2 + a.zzzz;
    vec4 k3 = perm(c);
    vec4 k4 = perm(c + 1.0);
    vec4 o1 = fract(k3 * (1.0 / 41.0));
    vec4 o2 = fract(k4 * (1.0 / 41.0));
    vec4 o3 = o2 * d.z + o1 * (1.0 - d.z);
    vec2 o4 = o3.yw * d.x + o3.xz * (1.0 - d.x);
    return o4.y * d.y + o4.x * (1.0 - d.y);
}

// 2D Noise for Dithering
float hash13(vec3 p3){
    p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

// fBm Noise
float fbm(vec3 x){
    float v = 0.0;
    float a = 0.5;
    for (int i = 0; i < NUM_OCTAVES; ++i){
        v += a * noise(x);
        x = x * 2.0;
        a *= 0.5;
    }
    return v;
}

// Colors
vec3 rgb2hsv(vec3 c){
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c){
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

// Nebula Noise
vec3 nebulanoise(vec3 raypos){
    float density = clamp(((fbm(raypos+(fbm(raypos)*DISTORT_AMOUNT))*16.0)-(fbm(raypos/2.0)*8.0))-pow(distance(vec3(0.0),raypos),2.0), 0.0, 1.0);
    vec3 rgb = hsv2rgb(rgb2hsv(vec3(density))+vec3((fbm(raypos+(fbm(raypos))*DISTORT_AMOUNT)/1.35)+0.25, 0.85, 0.0));
    return rgb;
}

// ##### RAYMARCHING #####

// Compute/March the Ray
vec3 raymarch(vec3 camerapos, vec3 raydir, vec2 coord){
    float distorigin=0.0;
    vec3 densityadd=vec3(0.0);
    vec3 density=vec3(0.0);
    vec3 raypos = camerapos;
    vec3 raydirmod = (raydir*(hash13(vec3(coord, iFrame))+1.0))*STEP_SIZE;
    for(int i=0; i<MAX_MARCHES; i++) {
        raypos = raypos + raydirmod;
        densityadd = nebulanoise(raypos)*DENSITY;
        density = vec3(density+densityadd);
        distorigin = raypos.z-camerapos.z;
        if(distorigin>MAX_DISTANCE) break;
    }
    return density;
}

// ##### RENDERING #####

// Render the Image
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // LoicVDB: I store the number of samples in the alpha channel
    // LoicVDB: take the previous color and sample count (black and 0 if on first frame)
    vec4 oldFragColor = vec4(0.);
    if(iFrame != 0) oldFragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);

    // LoicVDB:I put it in the fragColor var because I want to keep it even if it's not in a tile
    fragColor = oldFragColor;

    // LoicVDB's Hyper Epic Tiles Function
    int index = int(fragCoord.x/iResolution.x*float(TILES))
              + int(fragCoord.y/iResolution.y*float(TILES))*TILES;
    if(iFrame-(TILES*TILES*(iFrame/(TILES*TILES))) != index) return;

    // Camera Orientation
    vec3 xdir = vec3(1.0,0.0,0.0);
    vec3 ydir = vec3(0.0,1.0,0.0);
    vec3 zdir = vec3(0.0,0.0,1.0);
    float FOV = 1.0;
    vec3 camerapos = vec3(0.0, 0.0, -8.0);

    // Undistorted Normalized Pixel Coordinates (From 0 to 1)
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.x;
    vec2 uvd = fragCoord/iResolution.xy;
    vec3 raydir = normalize(FOV*(uv.x*xdir + uv.y*ydir) + zdir);
    vec3 raymarched = raymarch(camerapos, raydir, vec2(fragCoord));

    // LoicVDB: add the color and increase the sample count
    fragColor += vec4(raymarched, 1.0);
    if(isKeyPressed(32)){
        fragColor.rgba = vec4(0.0);
    }
}

// ^^^ Switch Between Buffer A and B ^^^
// This is for Exporting a 32-Bit Float OpenEXR using the Button on the Bottom of the Editor

vec3 acesFilm(const vec3 x){
    const float a = 2.51;
    const float b = 0.03;
    const float c = 2.43;
    const float d = 0.59;
    const float e = 0.14;
    return clamp((x*(a*x+b))/(x*(c*x+d)+e),0.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // LoicVDB: take the color from the A buffer
    vec4 texel = texelFetch(iChannel0, ivec2(fragCoord), 0);

    // LoicVDB: divide it by the sample count (the alpha channel)
    vec3 color = (texel.a == 0. ? vec3(0.) : texel.rgb / texel.a);

    // Output to screen
    fragColor = vec4(acesFilm(color), 1.0);
}
