float wave(float hz, float time)
{
    float offset = hz - 261.6256;
    float attenuation = clamp(0.0000174527 * offset * offset - 0.00609498 * offset + 0.7, 0.0, 1.0);
    return cos(6.28318531 * hz * time) * attenuation;
}

vec2 mainSound( in int samp, float time )
{
    float clamped = fract(time);

    float doo = 261.6256;
    float re = 293.6648;
    float mi = 329.6276;
    float fa = 349.2282;
    float so = 391.9954;
    float la = 440.0000;
    float si = 493.8833;
    float udo = 523.2511;

    float sum = 0.0;

    float t1 = sin(time / 2.0) > 0.0 ? mi : fa;
    float t2 = sin(time / 2.0) > 0.0 ? so : la;

    sum += wave(doo, clamped) * factor(time);
    sum += wave(t1, clamped) * factor(2.5 * time);
    sum += wave(t2, clamped) * factor(1.5 * time);
