vec4 color (vec2 U, vec2 r) {
	 vec4 c = C(U+r),
         a = A(unpack(c.z)), aa = A(unpack(c.w)),
        d = D(unpack(c.z)),dd = D(unpack(c.z));

    float o = length(aa.xy-a.xy),
          q = length(U-a.xy),
          w = sg(U,a.xy,aa.xy),
          v = pie(U,a.xy,aa.xy);
    return max(1.+0.5*(sign(unpack(c.z).x-0.5*R.x))*
               sin(mix(d.z,dd.z,.5)+vec4(3,2,1,4)),0.5)*
        (exp(-w)*exp(-.1*o)*.5+0.3*exp(-.2*q));

}
Main {Q = vec4(0);
    for (int x = -1; x<=1; x++)
        for (int y = -1; y<=1; y++) {
        	Q += color(U,vec2(x,y))/3./(1.+length(vec2(x,y)));
        }
}

#define R iResolution.xy
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texelFetch(iChannel1,ivec2(U),0)
#define C(U) texelFetch(iChannel2,ivec2(U),0)
#define D(U) texelFetch(iChannel3,ivec2(U),0)
#define Main void mainImage (out vec4 Q, vec2 U) 
#define Init if (iFrame < 1) 
#define Border if (U.x<1.||R.x-U.x<1.||U.y<1.||R.y-U.y<1.)
#define O 4.
#define shape(U) (round((U)/O)*O+0.5-1e-8)
#define N 12
#define Z vec2(u)
#define ro(a) mat2(cos(a),sin(-a),sin(a),cos(a))

#define unpack(i) vec2(mod(i,R.x),i/R.x)
#define pack(U) (floor((U).x)+floor((U).y)*R.x)
float angle (vec2 a, vec2 b) {
	return atan(a.x*b.y-a.y*b.x,dot(a.xy,b.xy));
}

float sg (vec2 p, vec2 a, vec2 b) {
    if (length(a-b)<1e-4||length(a)<1e-4||length(b)<1e-4) return 1e9;

    float i = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
	float l = (length(p-a-(b-a)*i));
    return l;
}
float ssg (vec2 p, vec2 a, vec2 b) {
    if (length(a-b)<1e-3||length(a)<1.||length(b)<1.) return 1e9;

    float i = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
	float l = (length(p-a-(b-a)*i));
    return (l+1.)*(1.+length(a-b));
}
vec2 pie2 (vec2 p, vec2 a, vec2 b) {
	if (length(a-b)<1e-3||length(a)<1.||length(b)<1.) return vec2(1e5);
    float i = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
	float l = (length(p-a-(b-a)*i));
    return vec2(l,length(p-a)); // pojection
} 
float pie (vec2 p, vec2 a, vec2 b) {
	 if (length(a-b)<1e-3||length(a)<1.||length(b)<1.) return 1e9;
    vec2 m = 0.5*(a+b); // midpoint
    return abs(dot(p-m,b-m)/dot(b-m,b-m)); // pojection
}

Main {
    vec2 u = shape(U);
    if (u.x+O>=R.x||u.y+O>=R.y||u.x<O||u.y<O) {Q = vec4(-1e3,-1e3,0,0);return;}
    Q = A(u);
    vec4 qd = D(u);
    vec2 f = vec2(0);
    float n = 0.;
    for (int x=-1;x<=1; x++) {
        for (int y=-1;y<=1; y++) {
            vec4 b = B(u+vec2(x,y));
            vec4
                a = A(b.xy),
                d = D(b.xy);
            vec2 r = (a.xy-Q.xy);
            float l = length(a.xy-Q.xy);
            if (l >1e-5&&a.x>3.) {
                f -= r*(d.z-qd.z)/l/l/l;
                float m = sign(U.x-0.5*R.x)*sign(b.x-0.5*R.x);
                f += .5*r*m/l/l/l;
                f -= 2.*r/l/l/l/l;
                n++;
            }
            a = A(b.zw);
            d = D(b.zw);
            r = (a.xy-Q.xy);
            l = length(a.xy-Q.xy);
            if (l >1e-5&&a.x>3.) {
                f -= r*(d.z-qd.z)/l/l/l;
                float m = sign(U.x-0.5*R.x)*sign(b.x-0.5*R.x);
                f += .5*r*m/l/l/l;
                f -= 2.*r/l/l/l/l;
                n++;
            }
        }
	}
    f /= n;
    Q.w -= 0./R.y;
    Q.zw += f;
    Q.xy += f+Q.zw*sqrt(1./(1.+dot(Q.zw,Q.zw)));

    if (length(Q.zw)>1.)Q.zw=1.*normalize(Q.zw);
    vec2 mouse = vec2(.3,.5)*R;
    if (iMouse.z>0.) mouse = iMouse.xy;
    Q.zw += vec2(.02,0)*smoothstep(20.,18.,length(mouse-Q.xy));
    if (Q.x<5.) {Q.x=5.;Q.z = +.5*abs(Q.z);}
    if (R.x-Q.x<5.) {Q.x=R.x-5.;Q.z =-.5*abs(Q.z);}
    if (Q.y<5.) {Q.y=5.;Q.w = +.5*abs(Q.w);}
    if (R.y-Q.y<5.) {Q.y=R.y-5.;Q.w =-.5*abs(Q.w);}
    if (iFrame < 20) {
    	Q = vec4(u+0.5*O,0,0);
    }

}

#define g .25
void X (inout vec4 Q, inout vec2 r, vec4 a, vec2 U, vec2 u, vec4 n) {
	float l = pie(a.xy+g*(U-u),a.xy,A(n.xy).xy),
          ll = pie(a.xy+g*(U-u),a.xy,A(n.zw).xy);
    if (l<r.x){
        r = vec2(l,r.x);
        Q=vec4(n.xy,Q.xy);
    } else if (l<r.y) {
    	r.y = l;
        Q.zw = n.xy;
    }
    if (ll<r.x){
    	r = vec2(ll,r.x);
        Q=vec4(n.zw,Q.xy);
    } else if (ll<r.y) {
    	r.y = ll;
        Q.zw = n.zw;
    }
}
Main {
    vec2 u = shape(U);
    vec4 a = A(u);
	Q = B(U);
    vec2 r = vec2(pie(a.xy+g*(U-u),a.xy,A(Q.xy).xy)
                 ,pie(a.xy+g*(U-u),a.xy,A(Q.zw).xy));

    for (int x = -2; x <= 2; x++) {
        for (int y = -2; y <= 2; y++) {
        	 vec4 c = C(a.xy+vec2(x,y));
    		X(Q,r,a,U,u,vec4(unpack(c.z),unpack(c.w)));
    		X(Q,r,a,U,u,B(U+vec2(x,y)));
        }
    }
    vec4 c = C(a.xy);
    X(Q,r,a,U,u,vec4(unpack(c.z),unpack(c.w)));
    X(Q,r,a,U,u,vec4(unpack(c.x),unpack(c.y)));
}

void X (inout vec4 Q, vec2 U, vec4 n) {
	float l = ssg(U,A(unpack(n.z)).xy,A(unpack(n.w)).xy),
          ll = ssg(U,A(unpack(Q.z)).xy,A(unpack(Q.w)).xy);
	if (l<ll) Q.zw = n.zw;
    l = ssg(U,A(unpack(Q.z)).xy,A(unpack(n.z)).xy);
    if (l<ll) Q.w = n.z;
    l = ssg(U,A(unpack(Q.x)).xy,A(unpack(n.y)).xy);
    if (l<ll) Q.y = n.y;
}
void Y (inout vec4 Q, vec2 U, vec4 n) {
	float l = ssg(U,A(unpack(n.x)).xy,A(unpack(n.y)).xy),
          ll = ssg(U,A(unpack(Q.x)).xy,A(unpack(Q.y)).xy);
	if (l<ll) Q.xy = n.xy;
    l = ssg(U,A(unpack(Q.x)).xy,A(unpack(n.x)).xy);
}
Main {
	Q = C(U).xyxy;
    vec2 u = shape(U);
    if (iFrame%N==0) {
        Q.xy = vec2(pack(u),pack(B(A(u).xy+u-U).xy));

    }
    else {
        float k = exp2(float(N-1-(iFrame%N)));
    	Y(Q,U,C(U+vec2(0,k)));
    	Y(Q,U,C(U+vec2(k,0)));
    	Y(Q,U,C(U-vec2(0,k)));
    	Y(Q,U,C(U-vec2(k,0)));
    }

	X(Q,U,C(U+vec2(0,1)));
    X(Q,U,C(U+vec2(1,0)));
    X(Q,U,C(U-vec2(0,1)));
    X(Q,U,C(U-vec2(1,0)));

	X(Q,U,C(U+vec2(0,2)));
    X(Q,U,C(U+vec2(2,0)));
    X(Q,U,C(U-vec2(0,2)));
    X(Q,U,C(U-vec2(2,0)));



    X(Q,U,C(U));
}

Main {
    vec2 u = shape(U);
    if (u.x+O>=R.x||u.y+O>=R.y||u.x<O||u.y<O) discard;
    Q = D(u);
    vec4 qa = A(u);
    vec2 f = vec2(0);
    float n = 0.;
    for (int x=-1;x<=1; x++) {
        for (int y=-1;y<=1; y++) {
            vec4 b = B(u+vec2(x,y)),
                a = A(b.xy),
                d = D(b.xy);
            vec2 r = a.xy-qa.xy;
            float l = length(r);
            if (l > 0.&&a.x>3.){
                f.x -= dot(r,a.zw-qa.zw)/l;
                //f.y -= dot(r.yx*vec2(-1,1),a.zw-qa.zw)/l/l;
                f.x += 2.*(d.z-Q.z)/l ;
                //f.y += (d.w-Q.w)/l;
                n++;
            }
            a = A(b.zw);
            d = D(b.zw);
            r = a.xy-qa.xy; 
            l = length(r);
            if (l > 1e-3&&a.x>3.) {
                f.x -= dot(r,a.zw-qa.zw)/l;
                //f.y -= dot(r.yx*vec2(-1,1),a.zw-qa.zw)/l/l;
                f.x += 2.*(d.z-Q.z)/l;
                //f.y += (d.w-Q.w)/l;
                n++;
            }
        }
	}
    f /= n;
    Q.zw += f;
    if (iFrame < 20) {
    	Q = vec4(0);
    }

}
