#define GLOW 0.7
#define NUM_PARTICLES 100.0

#define figureEightRadius -0.4 //Sign of value changes direction

vec3 particles(vec2 uv, vec3 color, float radius, float offset)
{        
    float amplitude = sin(iTime/6.0);
    //float amplitude = atan(iTime/6.0); //Continuous forward motion

    float x=sin((iTime*amplitude/offset)) * figureEightRadius*2.5*offset+ 0.9;
    float y=sin((iTime*amplitude*2.0/offset)) * figureEightRadius*1.1*offset+ 0.5;

    vec2 position = vec2(x,y);

    float dist = radius / distance(uv, position);
    return color * pow(dist, 0.95 / GLOW);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float radius= 0.006* (sin(iTime*.33)+2.1);

    // center pixel
    vec2 uv = (fragCoord.xy /iResolution.y);

    // Time varying pixel color
    vec3 color ;
    color.r = ((sin(((iTime)) * 0.15) + 0.05) * 0.4);
    color.g = ((sin(((iTime)) * 0.002)) * 0.2);
    color.b = ((sin(((iTime)) * 2.0) + 0.05) * 0.3);

    vec3 pixel ;

    for	(float i = 0.0; i < NUM_PARTICLES; i++)
        pixel += abs(particles(uv, color, radius, i / NUM_PARTICLES));

    fragColor = mix(vec4(uv,sin(iTime),1.0), vec4(pixel, 1.0), 0.8);
}
