const int AO_SAMPLES = 256;
const float AO_DIST = 0.15;
const float FIVETAP_K = 2.0;

const vec4 FLOOR_VEC       = vec4(0.0, 1.0, 0.0, 2.5);
const vec4 LEFT_WALL_VEC   = vec4(1.0, 0.0, 0.0, 5.0);
const vec4 RIGHT_WALL_VEC  = vec4(-1.0, 0.0, 0.0, 5.0);
const vec4 BACK_WALL_VEC   = vec4(0.0, 0.0, -1.0, 5.0);
const vec4 CEILING_VEC     = vec4(0.0, -1.0, 0.0, 7.5);
const vec3 LONG_BOX_POS    = vec3(2.1, -0.5, -2);
const vec3 LONG_BOX_SCALE  = vec3(1.5, 3.0, 1.5);
const vec3 SHORT_BOX_POS   = vec3(-2.1, 1, -1);
const vec3 SHORT_BOX_SCALE = vec3(1.5, 1.5, 1.5);

const vec3 EYE_POS         = vec3(0.0, 5, -18.0);
const vec3 REF_POS         = vec3(0.0, 2.5, 0.0);

const int RAY_STEPS     = 256;
const int FLOOR_ID      = 0;
const int LEFT_WALL_ID  = 1;
const int RIGHT_WALL_ID = 2;
const int BACK_WALL_ID  = 3;
const int CEILING_ID    = 4;
const int LONG_BOX_ID   = 5;
const int SHORT_BOX_ID  = 6;
const float PI_OVER_180 = 3.1415926 / 180.0;

struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;
};

void coordinateSystem(const vec3 v1, out vec3 v2, out vec3 v3) 
{

    if (abs(v1.x) > abs(v1.y)) 
    {
        v2 = vec3(-v1.z, 0, v1.x) / length(v1.xz);
    }
    else
    {
        v2 = vec3(0, v1.z, -v1.y) / length(v1.yz);
    }
    v3 = cross(v1, v2);
}

float plane(vec3 p, vec4 n)
{
  // n must be normalized
  return dot(p, n.xyz) + n.w;
}

// Box with side lengths b
float box(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}

vec3 rotateY(vec3 p, float a) 
{
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

vec3 rotateX(vec3 p, float amt) 
{
    return vec3(p.x, cos(amt) * p.y - sin(amt) * p.z, sin(p.y) + cos(p.z));
}


void sceneMap3D(vec3 pos, out float t, out int objId, vec3 lightPos)
{
    // floor
    t = plane(pos, FLOOR_VEC);  
    objId = FLOOR_ID;  

    float t2;
    if((t2 = plane(pos, LEFT_WALL_VEC)) < t)  
    {
        t= t2;
        objId = LEFT_WALL_ID;
    } 
    if((t2 = plane(pos, RIGHT_WALL_VEC)) < t)  
    {
        t= t2;
        objId = RIGHT_WALL_ID;
    }
    if((t2 = plane(pos, BACK_WALL_VEC)) < t)
    {
        t= t2;
        objId = BACK_WALL_ID;
    }
    if((t2 = plane(pos, CEILING_VEC)) < t)
    {
        t= t2;
        objId = CEILING_ID;
    }
    if((t2 = box(rotateY(pos + LONG_BOX_POS, 27.5 * PI_OVER_180), LONG_BOX_SCALE)) < t)  // long bix
    {
        t= t2;
        objId = LONG_BOX_ID;
    }
    if((t2 = box(rotateY(pos + SHORT_BOX_POS, -27.5 * PI_OVER_180), SHORT_BOX_SCALE)) < t)
    {
        t= t2;
        objId = SHORT_BOX_ID;
    }
}

float sceneMap3D(vec3 pos)
{
    float t = plane(pos, FLOOR_VEC);
    t = min(t, plane(pos, LEFT_WALL_VEC));
    t = min(t, plane(pos, RIGHT_WALL_VEC));
    t = min(t, plane(pos, BACK_WALL_VEC));
    t = min(t, plane(pos, CEILING_VEC));
    t = min(t, box(rotateY(pos + LONG_BOX_POS, 27.5 * PI_OVER_180), LONG_BOX_SCALE));
    t = min(t, box(rotateY(pos + SHORT_BOX_POS, -27.5 * PI_OVER_180), SHORT_BOX_SCALE));
    return t;
}

vec2 random2(vec3 p)
{
    return fract(sin(vec2(p.x, p.x + 1.0)) * vec2(43758.5453123, 22578.1459123));
}

// from https://www.shadertoy.com/view/3sB3zV
float computeAO(vec3 p, vec3 n, float dist)
{
    float aoSum = 0.0;

    // Sample a few points in the hemisphere around n at p
    vec3 t, b;

    // Make a tangent and bitangent vector
    coordinateSystem(n, t, b);

    for(int i = 0; i < AO_SAMPLES; ++i) 
    {
        // Generate a pair of random [0, 1] floats
        vec2 xi = random2(p + float(i) * 203.1);

        // Convert the xi pair to a vector in the hemisphere
        float len = sqrt(xi.y);
        float rx = len * cos(6.2831 * xi.x);
        float ry = len * sin(6.2831 * xi.x);
        float rz = sqrt(1.0 - xi.y); // z = sqrt(1 - x*x - y*y)
        vec3 dir = vec3(rx * t + ry * b + rz * n);

        aoSum += step(0.0, sceneMap3D(p + dir * dist));
    }

    return aoSum / float(AO_SAMPLES);
}

// from https://www.shadertoy.com/view/3sB3zV
float fiveTapAO(vec3 p, vec3 n, float k) 
{
    float aoSum = 0.0;
    for(float i = 0.0; i < 5.0; ++i)
    {
        float coeff = 1.0 / pow(2.0, i);
        aoSum += coeff * (i * AO_DIST - sceneMap3D(p + n * i * AO_DIST));
    }

    return 1.0 - k * aoSum;
}

void march(vec3 origin, vec3 dir, out float t, out int objId, vec3 lightPos)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, objId, lightPos);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }

    t = -1.0;
    objId = -1;
}

vec3 computeMaterial(int objId, vec3 p, vec3 n, vec3 light, vec3 view)
{
    float lambert = dot(light, n);

    switch(objId) 
    {
    case FLOOR_ID:  // floor
        return vec3(1.0, 1.0, 1.0) * lambert;
        break;
    case LEFT_WALL_ID:  // Left wall, which is a large red plane
        return vec3(0.63, 0.065, 0.05) * lambert;
        break;
    case RIGHT_WALL_ID:  // Right wall, which is a large green plane
        return vec3(0.14, 0.45, 0.091) * lambert;
        break;
    case BACK_WALL_ID:  // Back wall, which is a large white plane
        return vec3(1.0, 1.0, 1.0) * lambert;
        break;
    case CEILING_ID:  // Ceiling, which is a large white plane
        return vec3(1.0, 1.0, 1.0) * lambert;
        break;
    case LONG_BOX_ID:  // Cuboid, the long box
        return vec3(1.0, 1.0, 1.0) * lambert;
        break;
    case SHORT_BOX_ID:  // Cube, the short box
        return vec3(1.0, 1.0, 1.0) * lambert;
        break;
    default:
        return vec3(0.0);
        break;
    }

    return vec3(1.0);
}

vec3 computeNormal(vec3 pos, vec3 lightPos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize(vec3(sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                          sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                          sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}

Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos)
{
    float t;
    int objId;
    march(eye, dir, t, objId, lightPos);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect, lightPos);
    vec3 lightDir = normalize(lightPos - isect);
    vec3 surfaceColor = computeMaterial(objId, isect, nor, lightDir, normalize(eye - isect));

    float ao = fiveTapAO(isect, nor, FIVETAP_K);
    vec3 sdfColor = ao * surfaceColor;

    return Intersection(t, sdfColor, isect, objId);
}

void raycast(vec2 uv, out vec3 dir, out vec3 eye, out vec3 ref)
{
    eye = EYE_POS;
    ref = REF_POS;

    float len = tan(3.14159 * 0.125) * distance(eye, ref);
    vec3 H = normalize(cross(vec3(0.0, 1.0, 0.0), ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * float(iResolution.x) / float(iResolution.y);
    vec3 p = ref + uv.x * H + uv.y * V;
    dir = normalize(p - eye);
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 lightPos = vec3(0.0, 7.3, -1.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // [-1, 1]
    vec2 uv2 = 2.0 * uv - vec2(1.0);

    vec3 dir, eye, ref;
    raycast(uv2, dir, eye, ref);
    Intersection intersection = sdf3D(dir, eye, lightPos);

    // Output to screen
    fragColor = vec4(intersection.color, 1.0);
}
